;s:10:"datePrefix";s:8:"datatype";s:16:"untrimmed string";s:11:"description";s:22:"Prefix for header date";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:10:"linkPrefix";s:8:"datatype";s:16:"untrimmed string";s:11:"description";s:23:"Prefix for header links";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:16:"[1-5].preLineLen";s:8:"datatype";s:3:"int";s:11:"description";s:28:"Lenght of line before header";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:17:"[1-5].postLineLen";s:8:"datatype";s:3:"int";s:11:"description";s:27:"Lenght of line after header";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:15:"[1-5].preBlanks";s:8:"datatype";s:3:"int";s:11:"description";s:35:"Number of blank lines before header";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:16:"[1-5].postBlanks";s:8:"datatype";s:3:"int";s:11:"description";s:34:"Number of blank lines after header";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:15;a:6:{s:8:"property";s:13:"[1-5].stdWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:15:"for header text";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:16;a:6:{s:8:"property";s:17:"[1-5].preLineChar";s:8:"datatype";s:6:"string";s:11:"description";s:21:"Character to pre-line";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:17;a:6:{s:8:"property";s:18:"[1-5].postLineChar";s:8:"datatype";s:6:"string";s:11:"description";s:22:"Character to post-line";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:18;a:6:{s:8:"property";s:19:"[1-5].preLineBlanks";s:8:"datatype";s:3:"int";s:11:"description";s:49:"Number of blank lines between header and pre-line";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:19;a:6:{s:8:"property";s:20:"[1-5].postLineBlanks";s:8:"datatype";s:3:"int";s:11:"description";s:50:"Number of blank lines between header and post-line";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:20;a:6:{s:8:"property";s:16:"[1-5].autonumber";s:8:"datatype";s:7:"boolean";s:11:"description";s:111:"If set, a number is prepended every header. The number corresponds to the content element number in the select.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:21;a:6:{s:8:"property";s:12:"[1-5].prefix";s:8:"datatype";s:16:"untrimmed string";s:11:"description";s:20:"Header string prefix";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:22;a:6:{s:8:"property";s:23:"bulletlist.[0-3].bullet";s:8:"datatype";s:16:"untrimmed string";s:11:"description";s:36:"Bullet for bullet list, layout [0-3]";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:23;a:6:{s:8:"property";s:26:"bulletlist.[0-3].secondRow";s:8:"datatype";s:16:"untrimmed string";s:11:"description";s:65:"If set, this is used for lines on the second row of bullet-lists.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:24;a:6:{s:8:"property";s:4:"menu";s:8:"datatype";s:7:"cObject";s:11:"description";s:137:"cObject to render menu. The output is stripped for tags and the links is extracted. Further all &lt;BR&gt; chars are converted to chr(10)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:25;a:6:{s:8:"property";s:8:"shortcut";s:8:"datatype";s:7:"cObject";s:11:"description";s:129:"cObject to render other elements. See config below which simply uses this object to render more tt_content elements as plaintext.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:26;a:6:{s:8:"property";s:16:"bodytext.stdWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:48:"stdWrap for body-text. See config example below.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:27;a:6:{s:8:"property";s:8:"userProc";s:8:"datatype";s:13:"function-name";s:11:"description";s:195:"Lets you proces the output of each content element before it finally is returned. Property &quot;parentObj&quot; of the conf-array holds a references to the plainText object calling the function.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('171', '270', '9a0c31e828f7b074a48c5e5a788dfa6c', '', 'tsref:-&gt;tableStyle', 'a:1:{s:4:"rows";a:7:{i:0;a:6:{s:8:"property";s:5:"align";s:8:"datatype";s:14:"align /stdWrap";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"border";s:8:"datatype";s:12:"int /stdWrap";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:11:"cellspacing";s:8:"datatype";s:12:"int /stdWrap";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:11:"cellpadding";s:8:"datatype";s:12:"int /stdWrap";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:11:"color.field";s:8:"datatype";s:6:"string";s:11:"description";s:46:"Set to fieldname from the $cObj-&gt;data-array";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:29:"color.default\ncolor.1\ncolor.2";s:8:"datatype";s:6:"string";s:11:"description";s:39:"[default],[1],[2] = User defined\n&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:6:"params";s:8:"datatype";s:20:"&lt;TABLE&gt;-params";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('232', '270', '397a0804fc8ada0e121f0a17e8bf8dd4', '', 'tsref:(cObject).HMENU.(mObj).GMENU', 'a:1:{s:4:"rows";a:18:{i:0;a:6:{s:8:"property";s:2:"RO";s:8:"datatype";s:7:"Boolean";s:11:"description";s:155:"RollOver configuration enabled / disabled.\nIf this is true, RO becomes a GIFBUILDER-object defining the layout of the menuitem when the mouse rolls over it";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"expAll";s:8:"datatype";s:7:"Boolean";s:11:"description";s:252:"If this is true, the menu will always show the menu on the level underneath the menuitem. This corresponds to a situation where a user has clicked a menuitem and the menu folds out the next level. This can enable that to happen on all items as default.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:8:"collapse";s:8:"datatype";s:7:"Boolean";s:11:"description";s:116:"If set, &quot;active&quot; menuitems that has expanded the next level on the menu will now collapse that menu again.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:9:"accessKey";s:8:"datatype";s:7:"Boolean";s:11:"description";s:44:"If set access-keys are set on the menu-links";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:6:"noBlur";s:8:"datatype";s:7:"Boolean";s:11:"description";s:218:"Normally graphical links are &quot;blurred&quot; if the browser is MSIE. Blurring removes the ugly box around a clicked link.\nIf this property is set, the link is NOT blurred (browser-default) with &quot;onFocus&quot;.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:6:"target";s:8:"datatype";s:6:"target";s:11:"description";s:23:"Target of the menulinks";s:7:"default";s:4:"self";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:14:"forceTypeValue";s:8:"datatype";s:3:"int";s:11:"description";s:141:"If set, the &amp;type parameter of the link is forced to this value regardless of target. Overrides the global equivalent in \'config\' if set.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:4:"wrap";s:8:"datatype";s:4:"wrap";s:11:"description";s:43:"Wraps only if there were items in the menu!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:11:"applyTotalH";s:8:"datatype";s:20:"objNumsList (offset)";s:11:"description";s:480:"This adds the total height of the previously generated menuitems to the offset of the GifBuilderObj\'s mentioned in this list.\n&nbsp;\n<b>Example:</b>\nThis is useful it you want to create a menu with individual items but a common background image that extends to the whole area behind the menu. Then you should setup the background image in each GIFBUILDER-object and include the object-number in this list.\nLook at the implementation in static_template &quot;styles.gmenu.bug&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:11:"applyTotalW";s:8:"datatype";s:20:"objNumsList (offset)";s:11:"description";s:124:"This adds the total width of the previously generated menuitems to the offset of the GifBuilderObj\'s mentioned in this list.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:3:"min";s:8:"datatype";s:13:"x,y (calcInt)";s:11:"description";s:54:"Forces the menu as a whole to these minimum dimensions";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:3:"max";s:8:"datatype";s:13:"x,y (calcInt)";s:11:"description";s:54:"Forces the menu as a whole to these maximum dimensions";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:15:"useLargestItemX";s:8:"datatype";s:7:"boolean";s:11:"description";s:81:"If set, then the width of all menuitems will be equal to the largest of them all.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:15:"useLargestItemY";s:8:"datatype";s:7:"boolean";s:11:"description";s:82:"If set, then the height of all menuitems will be equal to the largest of them all.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:20:"removeObjectsOfDummy";s:8:"datatype";s:11:"objNumsList";s:11:"description";s:259:"If the menu is forced to a certain minimum dimension, this is a list of objects in the gifbuilder-object that is removed for this last item. This is important to do if the menuitems has elements that should only be applied if the item is actually a menuitem!!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:15;a:6:{s:8:"property";s:14:"disableAltText";s:8:"datatype";s:7:"boolean";s:11:"description";s:112:"If set, the alt-parameter of the images are not set. You can do it manually by &quot;imgParams&quot; (see below)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:16;a:6:{s:8:"property";s:9:"IProcFunc";s:8:"datatype";s:13:"function-name";s:11:"description";s:327:"The internal array &quot;I&quot; is passed to this function and expected returned as well. Subsequent to this function call the menu item is compiled by implode()\'ing the array $I[parts] in the passed array. Thus you may modify this if you need to.\nSee example on the testsite and in media/scripts/example_itemArrayProcFunc.php";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:17;a:6:{s:8:"property";s:70:"[Common Item States, see above]\n+ rollover version for all, except SPC";s:8:"datatype";s:364:"-&gt;GIFBUILDER\n+ .wrap (= wrap)\n+ .noLink (boolean, if set, the item is NOT linked!)\n+.imgParams (params: parameters for the &lt;img&gt;-tag)\n+ .altTarget which overrides the target defined for the GMENU.\n+ .altImgResource (datatype = imgResource) which defines an alternative image to use. If an image returns here, it will override any GIFBUILDER configuration.";s:11:"description";s:528:"This is the GIFBUILDER-options for each category of menuitem that can be generated.\n&nbsp;\n<b>NOTE:</b> For the GMENU series you can also define the RollOver configuration for the item states. This means that you define the GIFBUILDER object for the \'Active\' state by ACT and the RollOver GIFBUILDER object for the \'Active\' state by ACTRO.\nThis pattern goes for ALL the states except the SPC state.\n&nbsp;\n<b>SPECIAL:</b>\nThe -&gt;OptionSplit function is run on the whole GIFBUILDER-configuration before the items are generated.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('231', '270', '2c8d1fb6db707d441cd5769786710a19', '', 'tsref:(cObject).HMENU.(mObj_itemStates)', 'a:1:{s:4:"rows";a:8:{i:0;a:6:{s:8:"property";s:5:"IFSUB";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:54:"Enable/Configuration for menu items which has subpages";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:3:"ACT";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:67:"Enable/Configuration for menu items which are found in the rootLine";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:8:"ACTIFSUB";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:84:"Enable/Configuration for menu items which are found in the rootLine and has subpages";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:3:"CUR";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:69:"Enable/Configuration for a menu item if the item is the current page.";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:3:"USR";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:96:"Enable/Configuration for menu items which are access restricted pages that a user has access to.";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:3:"SPC";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:265:"Enable/Configuration for \'Spacer\' pages.\nSpacers are pages of the doktype &quot;Spacer&quot;. These are not viewable pages but &quot;placeholders&quot; which can be used to devide menuitems.\n&nbsp;\n<b>Note:</b> Rollovers doesn\'t work with spacers, if you use GMENU!";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:8:"USERDEF1";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:277:"Userdefined, see .itemArrayProcFunc for details on how to use this.\nYou can set the ITEM_STATE values USERDEF1 and USERDEF2 (+...RO) from a script/userfunction processing the menu item array. See HMENU/special=userdefined or the property .itemArrayProcFunc of the menu objects.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:8:"USERDEF2";s:8:"datatype";s:18:"Boolean / (config)";s:11:"description";s:11:"(See above)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('179', '270', '2bcf41f3a27a1da90a09ebdf3a40f315', '', 'tsref:(TLO)', 'a:1:{s:4:"rows";a:10:{i:0;a:6:{s:8:"property";s:5:"types";s:8:"datatype";s:8:"readonly";s:11:"description";s:55:"Types (internal)\ntype=99 reserved for plaintext display";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:9:"resources";s:8:"datatype";s:8:"readonly";s:11:"description";s:28:"Resources in list (internal)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:9:"sitetitle";s:8:"datatype";s:8:"readonly";s:11:"description";s:20:"SiteTitle (internal)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:6:"config";s:8:"datatype";s:11:"-&gt;CONFIG";s:11:"description";s:131:"Global configuration.\nThese values are stored with cached pages which means they are also accessible when retrieving a cached page.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:9:"constants";s:8:"datatype";s:14:"-&gt;CONSTANTS";s:11:"description";s:183:"Site-specific constants, eg. a general email-adresse. These constants may be substituted in the text throughout the pages. The substitution is done by parseFunc. (Option: constants=1)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:6:"FEData";s:8:"datatype";s:19:"<i>-&gt;FE_DATA</i>";s:11:"description";s:105:"Here you can configure how data submitted from the front-end should be processed, which script and so on.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:11:"includeLibs";s:8:"datatype";s:23:"<i>array of strings</i>";s:11:"description";s:156:"With this you can include php-files with function libraries for use in your includescript in Typo3.\nPlease see the PAGE-object, which has the same property.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:56:"Other reserved TLO\'s:\n&nbsp;\nplugin\ntt_*\ntemp\nstyles\nlib";s:8:"datatype";s:13:"<i>&nbsp;</i>";s:11:"description";s:828:"These toplevel object names are reserved. That means you can risk static_templates to use them:\n&quot;plugin&quot; is used for rendering of special content like boards, ecommerce solutions, guestbooks and so on. Normally set from static_templates. <i>Please see separate description below!</i>\n&quot;tt_*&quot;, eg tt_content (from &quot;content (default)&quot;) is used to render content from tables.\n&quot;temp&quot; and &quot;styles&quot; are used for conde-libraries you can copy during parse-time, but they are not saved with the template in cache. <b>&quot;temp&quot; / &quot;styles&quot; are unset</b> before the template is cached! Therefore use these names to store temporary data.\n&quot;lib&quot; can be used for a &quot;library&quot; of code, you can reference in TypoScript (unlike &quot;styles&quot; which is unset)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:3:"...";s:8:"datatype";s:4:"PAGE";s:11:"description";s:379:"Start a new page\n&nbsp;\n<b>Example:</b>\n&nbsp;\n<b>Guidelines:</b>\nGood, general PAGE object names to use are such as:\n<i>page </i>for the main page with content\n<i>frameset, frameset2 </i>for framesets.\n<i>top, left, menu, right, bottom, border </i>for top and menu frames etc.\nThis is just recommandations. Especially the name \'page\' for the content bearing page is very common.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:3:"...";s:8:"datatype";s:17:"<i>(whatever)</i>";s:11:"description";s:365:"If a toplevel-object is not a PAGE-object it could be used as a temporary repository for setup. In this case you should use the &quot;temp&quot; or &quot;styles&quot; objects.\n&quot;tt_...&quot; is normally used to define the setup of content-records. Eg. &quot;tt_content&quot; would be used for the tt_content-table as default. See the &quot;CONTENT&quot;-cObject";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('180', '270', '270df189982a4482d7b570baa95cba05', '', 'tsref:plugin', 'a:1:{s:4:"rows";a:5:{i:0;a:6:{s:8:"property";s:8:"userFunc";s:8:"datatype";s:13:"<i>&nbsp;</i>";s:11:"description";s:67:"<i>Property setting up the USER / USER_INT object of the plugin</i>";s:7:"default";s:13:"<i>&nbsp;</i>";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:11:"includeLibs";s:8:"datatype";s:13:"<i>&nbsp;</i>";s:11:"description";s:67:"<i>Property setting up the USER / USER_INT object of the plugin</i>";s:7:"default";s:13:"<i>&nbsp;</i>";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:18:"_CSS_DEFAULT_STYLE";s:8:"datatype";s:6:"string";s:11:"description";s:328:"Use this to have some default CSS styles inserted in the header section of the document. Most likely this will provide a default acceptable display from the plugin, but should ideally be cleared and moved to an external stylesheet.\nThis value is for all plugins read by the pagegen script when making the header of the document.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:28:"_DEFAULT_PI_VARS.[piVar-key]";s:8:"datatype";s:6:"string";s:11:"description";s:202:"Allows you to set default values of the piVars array which most plugins are using (and should use) for data exchange with themselves.\nThis works only if the plugin calls $this-&gt;pi_setPiVarDefaults().";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:34:"_LOCAL_LANG.[lang-key].[label-key]";s:8:"datatype";s:6:"String";s:11:"description";s:68:"Can be used to override the default locallang labels for the plugin.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('181', '270', '2a74eafe437239f6dfa39d7289a1e6d9', '', 'tsref:config/-&gt;CONFIG', 'a:1:{s:4:"rows";a:72:{i:0;a:6:{s:8:"property";s:8:"linkVars";s:8:"datatype";s:4:"list";s:11:"description";s:312:"HTTP_GET_VARS, which should be passed on with links in Typo. This is compiled into a string stored in $GLOBALS[&quot;TSFE&quot;]-&gt;linkVars\n&nbsp;\n<b>Example:</b>\n&nbsp;\nThis will add &quot;&amp;noframes=[noframes-value]&amp;print=[print-value]&quot; to all links in Typo3.\nThe values are rawurlencoded in PHP.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:11:"MP_defaults";s:8:"datatype";s:6:"string";s:11:"description";s:322:"Allows you to set a list of page id numbers which will always have a certain &quot;&amp;MP=...&quot; parameter added.\n&nbsp;\n<b>Syntax:</b>\n[id],[id],... : [MP-var] | [id],[id],... : [MP-var] | ...\n&nbsp;\n<b>Example:</b>\n&nbsp;\nThis will by default add &quot;&amp;MP=2-207&quot; to all links pointing to pages 36,37 and 48";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:11:"metaCharset";s:8:"datatype";s:6:"string";s:11:"description";s:115:"Charset used in the meta-tag:\n&lt;meta http-equiv=&quot;Content-Type&quot; content=&quot;text/html; charset=...&gt;";s:7:"default";s:10:"iso-8859-1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:10:"ATagParams";s:8:"datatype";s:16:"&lt;A&gt;-params";s:11:"description";s:112:"Additional parameters to all links in Typo3 (excluding menu-links)\n&nbsp;\n<b>Example:</b>\nTo blur links, insert:";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:13:"setJS_openPic";s:8:"datatype";s:7:"boolean";s:11:"description";s:64:"If set, the openPic JavaScript function is forced to be included";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:15:"setJS_mouseOver";s:8:"datatype";s:7:"boolean";s:11:"description";s:75:"If set, the over() and out() JavaScript functions are forced to be included";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:3:"ftu";s:8:"datatype";s:7:"boolean";s:11:"description";s:856:"If set, the &quot;&amp;ftu=....&quot; GET-fallback identification is inserted.\n&quot;&amp;ftu=[hash]&quot; is always inserted in the links on the first page a user hits. If it turns out in the next hit that the user has cookies enabled, this variable is not set anymore as the cookies does the job. If no cookies is accepted the &quot;ftu&quot; remains set for all links on the site and thereby we can still track the user.\n&nbsp;\n<b>You should not set this feature if grabber-spiders like Teleport are going to grab your site!</b>\n<b>You should not set this feature if you want search-engines to index your site (in conjunction with the simulateStaticDocuments feature!)</b>\n<b>&nbsp;</b>\nYou can also ignore this feature if you\'re certain, website users will use cookies.\n&nbsp;&quot;ftu&quot; means fe_typo_user (&quot;fe&quot; is &quot;frontend&quot;).";s:7:"default";s:5:"false";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:10:"mainScript";s:8:"datatype";s:6:"string";s:11:"description";s:240:"This lets you specify an alternative &quot;mainScript&quot; which is the document that Typo3 expects to be the default doc. This is used in form-tags and other places where Typo3 needs to refer directly to the main-script of the application";s:7:"default";s:9:"index.php";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:13:"pageGenScript";s:8:"datatype";s:8:"resource";s:11:"description";s:569:"Alternative page generation script for applications using index_ts.php for initialization, caching, stating and so on. This script is included in the global scope of index_ts.php-script and thus you may include libraries here. Always use include_once for libraries.\nRemember not to output anything from such an included script. <b>All content must be set into $TSFE-&gt;content. </b>Take a look at tslib/pagegen.php\n&nbsp;\n<b>NOTE:</b> This option is ignored if \n&nbsp;\n$TYPO3_CONF_VARS[&quot;FE&quot;][&quot;noPHPscriptInclude&quot;]=1;\n&nbsp;\nis set in localconf.php.";s:7:"default";s:17:"tslib/pagegen.php";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:5:"debug";s:8:"datatype";s:7:"boolean";s:11:"description";s:110:"If set any debug-information in the TypoScript code is output. Currently this applies only to the menu-objects";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:31:"message_page_is_being_generated";s:8:"datatype";s:6:"string";s:11:"description";s:191:"Alternative message in HTML that appears if a page is being generated.\nNormally when a page is being generated a temporary copy is stored in the cache-table with an expire-time of 30 seconds.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:15:"message_preview";s:8:"datatype";s:6:"string";s:11:"description";s:77:"Alternative message in HTML that appears when the preview function is active!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:10:"locale_all";s:8:"datatype";s:6:"string";s:11:"description";s:312:"PHP: setlocale(&quot;LC_ALL&quot;, [value]);\nvalue-examples: deutsch, de_DE, danish, portuguese, spanish, french, norwegian, italian. See www.php.net for other value. Also on linux, look at /usr/share/locale/\n&nbsp;\n<b>Example:</b>\nThis will render dates in danish made with stdWrap/strftime:\nlocale_all = danish";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:16:"sword_standAlone";s:8:"datatype";s:7:"boolean";s:11:"description";s:136:"Used by the parseFunc-substitution of search Words (sword):\nIf set, the words MUST be surrounded by whitespace in order to be marked up.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:17:"sword_noMixedCase";s:8:"datatype";s:7:"boolean";s:11:"description";s:129:"Used by the parseFunc-substitution of search Words (sword):\nIf set, the words MUST be the exact same case as the search word was.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:15;a:6:{s:8:"property";s:9:"intTarget";s:8:"datatype";s:6:"target";s:11:"description";s:61:"default internal target. Used by typolink if no target is set";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:16;a:6:{s:8:"property";s:9:"extTarget";s:8:"datatype";s:6:"target";s:11:"description";s:64:"default external target. Used by typolink if no extTarget is set";s:7:"default";s:4:"_top";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:17;a:6:{s:8:"property";s:25:"spamProtectEmailAddresses";s:8:"datatype";s:17:"boolean / -5 to 5";s:11:"description";s:388:"If set, then all email addresses in typolinks will be encrypted so spam bots cannot detect them. Encryption is a simply offset of character values. If you set this value to &quot;3&quot; then all characters will have their ASCII value offset by &quot;3&quot;. The reason for supplying this is... well just a way you can choose another offset than everyone else... Hmm. Does it make sense?";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:18;a:6:{s:8:"property";s:33:"spamProtectEmailAddresses_atSubst";s:8:"datatype";s:6:"string";s:11:"description";s:37:"Substitute label for the at-sign (@).";s:7:"default";s:4:"(at)";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:19;a:6:{s:8:"property";s:14:"forceTypeValue";s:8:"datatype";s:3:"int";s:11:"description";s:326:"Force the &amp;type value of all Typo3 generated links to a specific value (except if overruled by local forceTypeValue values).\nUseful if you run a template with special content at - say &amp;type=95 - but still wants to keep your targets neutral. Then you set your targets to blank and this value to the type value you wish.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:20;a:6:{s:8:"property";s:26:"frameReloadIfNotInFrameset";s:8:"datatype";s:7:"boolean";s:11:"description";s:372:"If set, then the current page will check if the page object name (eg. &quot;page&quot; or &quot;frameset&quot;) exists as &quot;parent.[name]&quot; (eg. &quot;parent.page&quot;) and if not the page will be reloaded in top frame. This secures that links from search engines to pages inside a frameset will load the frameset.\nWorks only with type-values different from zero.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:21;a:6:{s:8:"property";s:14:"jumpurl_enable";s:8:"datatype";s:7:"boolean";s:11:"description";s:217:"Jumpurl is a concept where external links are redirected from the index_ts.php script, which first logs which url it was. This logging of external links is only interesting if you use the internal stat-table in Typo3.";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:22;a:6:{s:8:"property";s:22:"jumpurl_mailto_disable";s:8:"datatype";s:7:"boolean";s:11:"description";s:59:"Disables the use of jumpUrl when linking to email-adresses.";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:23;a:6:{s:8:"property";s:20:"compensateFieldWidth";s:8:"datatype";s:6:"double";s:11:"description";s:544:"this floating point value will be used by the FORMS cObject to compensate the length of the formfields text and input.\nThis feature is useful, if the page-option &quot;smallFormFields&quot; is set. In that case Netscape renders formfields much longer than IE. If you want the two browsers to display the same size formfields, use a value of approx &quot;0.6&quot; for netscape-browsers.\n&nbsp;\n<b>Example:</b>\n[browser = netscape]\n&nbsp;&nbsp;config.compensateFieldWidth = 0.6\n[global]\n&nbsp;\nThis option may be overridden in the FORMS-cObject.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:24;a:6:{s:8:"property";s:14:"includeLibrary";s:8:"datatype";s:8:"resource";s:11:"description";s:24:"This includes a phpfile.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:25;a:6:{s:8:"property";s:17:"incT3Lib_htmlmail";s:8:"datatype";s:7:"boolean";s:11:"description";s:38:"Include t3lib/class.t3lib_htmlmail.php";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:26;a:6:{s:8:"property";s:12:"lockFilePath";s:8:"datatype";s:6:"string";s:11:"description";s:102:"This is used to lock paths to be &quot;inside&quot; this path.\nUsed by &quot;filelist&quot; in stdWrap";s:7:"default";s:10:"fileadmin/";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:27;a:6:{s:8:"property";s:9:"noScaleUp";s:8:"datatype";s:7:"boolean";s:11:"description";s:352:"Normally images are scaled to the size specified via TypoScript. This also forces small images to be scaled to a larger size. This is not always a good thing.\nIf this property is set, images are <b>not</b> allowed to be scaled up in size. This parameter clears the $this-&gt;mayScaleUp var of the class t3lib_stdgraphics (often &quot;gifbuilder&quot;).";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:28;a:6:{s:8:"property";s:19:"USERNAME_substToken";s:8:"datatype";s:6:"string";s:11:"description";s:169:"The is the token used on the page, which should be substituted with the current username IF a front-end user is logged in! If no login, the substitution will not happen.";s:7:"default";s:27:"&lt;!--###USERNAME###--&gt;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:29;a:6:{s:8:"property";s:12:"cache_period";s:8:"datatype";s:12:"int, seconds";s:11:"description";s:179:"The number of second a page may remain in cache.\nThis value is overridden by the value set in the page-record (field=&quot;cache_timeout&quot;) if this value is greater than zero.";s:7:"default";s:12:"86400 (=24H)";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:30;a:6:{s:8:"property";s:21:"cache_clearAtMidnight";s:8:"datatype";s:7:"boolean";s:11:"description";s:99:"With this setting the cache always expires at midnight of the day, the page is scheduled to expire.";s:7:"default";s:5:"false";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:31;a:6:{s:8:"property";s:8:"no_cache";s:8:"datatype";s:7:"boolean";s:11:"description";s:142:"If this is set to true, the page will not be cached. If set to false, it\'s ignored. Other parameters may have set it to true of other reasons.";s:7:"default";s:1:"-";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:32;a:6:{s:8:"property";s:20:"disableAllHeaderCode";s:8:"datatype";s:7:"boolean";s:11:"description";s:407:"If this is set, none of the features of the PAGE-object is processed and the content of the page will be the result of the cObject array (1,2,3,4...) of the PAGE-object. &nbsp;This means that the result of the cObject should include everything from the &lt;HTML&gt; .... to the &lt;/HTML&gt; tag !!\nUse this feature in templates supplying other content-types than HTML. That could be an image or a WAP-page!";s:7:"default";s:5:"false";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:33;a:6:{s:8:"property";s:17:"additionalHeaders";s:8:"datatype";s:32:"strings divided by &quot;|&quot;";s:11:"description";s:495:"This is additional headers. You separate each header by a vertical line &quot;|&quot;. &nbsp;Normally Typo3 does <i>not</i> send any headers with the Header()-function in PHP.\n&nbsp;\n<b>Examples:</b>\nContent-type: text/vnd.wap.wml\n(this will sent a content-header for a WAP-site)\n&nbsp;\nContent-type: image/gif | Expires: Mon, 26 Jul 1997 05:00:00 GMT\n(this will sent a content-header for a GIF-file and a Expires header)\n&nbsp;\nLocation: www.typo3.com\n(This redirects the page to www.typo3.com)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:34;a:6:{s:8:"property";s:22:"disablePageExternalUrl";s:8:"datatype";s:7:"boolean";s:11:"description";s:152:"If set, pages with doktype &quot;External Url&quot; will not trigger jumpUrl in TSFE. This may help you to have external urls open inside you framesets.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:35;a:6:{s:8:"property";s:4:"stat";s:8:"datatype";s:7:"boolean";s:11:"description";s:27:"Enable stat logging at all.";s:7:"default";s:4:"true";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:36;a:6:{s:8:"property";s:16:"stat_typeNumList";s:8:"datatype";s:8:"int/list";s:11:"description";s:275:"List of pagetypes that should be registered in the statistics table, sys_stat.\nIf no types are listed, all types are logged.\nDefault is &quot;0,1&quot; which normally logs all hits on framesets and hits on content keeping pages. Of course this depends on the template design.";s:7:"default";s:3:"0,1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:37;a:6:{s:8:"property";s:22:"stat_excludeBEuserHits";s:8:"datatype";s:7:"boolean";s:11:"description";s:65:"If set a pagehit is not logged if a user is logged in into Typo3.";s:7:"default";s:5:"false";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:38;a:6:{s:8:"property";s:18:"stat_excludeIPList";s:8:"datatype";s:15:"list of strings";s:11:"description";s:72:"If the REMOTE_ADDR is in the list of IP-addresses, it\'s also not logget.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:39;a:6:{s:8:"property";s:10:"stat_mysql";s:8:"datatype";s:7:"boolean";s:11:"description";s:43:"Enable logging to the MySQL table sys_stat.";s:7:"default";s:5:"false";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:40;a:6:{s:8:"property";s:11:"stat_apache";s:8:"datatype";s:7:"boolean";s:11:"description";s:61:"Enable logging to the logfile &quot;stat_apache_logfile&quot;";s:7:"default";s:5:"false";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:41;a:6:{s:8:"property";s:19:"stat_apache_logfile";s:8:"datatype";s:8:"filename";s:11:"description";s:326:"This the filename of the logfile in the directory $TYPO3_CONF_VARS[&quot;FE&quot;][&quot;logfile_dir&quot;] (localconf) on the server where Typo3 writes to the apachelogfile. The directory defined by &nbsp;$TYPO3_CONF_VARS[&quot;FE&quot;][&quot;logfile_dir&quot;] &nbsp;must exist and be writable. The logfile also must exist.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:42;a:6:{s:8:"property";s:21:"stat_apache_pagenames";s:8:"datatype";s:6:"string";s:11:"description";s:285:"The &quot;pagename&quot; simulated for apache.\nDefault: &nbsp;&nbsp;&nbsp;&quot;[path][title]--[uid].html&quot;\nCodes:\n[title] = inserts title, no special characters and shortend to 30 chars.\n[uid] = the id\n[alias] = any alias\n[type] = the type (typeNum)\n[path] = the path of the page.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:43;a:6:{s:8:"property";s:23:"stat_apache_notExtended";s:8:"datatype";s:7:"boolean";s:11:"description";s:60:"If true the logfile is NOT written in Apache extended format";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:44;a:6:{s:8:"property";s:18:"stat_apache_noHost";s:8:"datatype";s:7:"boolean";s:11:"description";s:80:"If true the HTTP_HOST is - if available - NOT inserted instead of the IP-address";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:45;a:6:{s:8:"property";s:13:"stat_titleLen";s:8:"datatype";s:9:"int 1-100";s:11:"description";s:68:"The lenght of the page names in the path written to logfile/database";s:7:"default";s:2:"20";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:46;a:6:{s:8:"property";s:23:"simulateStaticDocuments";s:8:"datatype";s:39:"boolean /\nstring: &quot;PATH_INFO&quot;";s:11:"description";s:1470:"If set Typo3 makes all links in another way than usual. This can be used with <b>Apache compiled with mod_rewrite and configured in httpd.conf for use of this in the &quot;.htaccess&quot;-files.</b>\nInclude this in the .htaccess file\n&nbsp;&nbsp;&nbsp;&nbsp;RewriteEngine On\n&nbsp;&nbsp;&nbsp;&nbsp;RewriteRule &nbsp;&nbsp;^[^/]*\\.html$ &nbsp;index.php\n&nbsp;\nThis means that any &quot;*.html&quot;-documents should be handled by index.php.\nNow if is done, Typo3 will interprete the url of the html-document like this:\n&nbsp;&nbsp;&nbsp;&nbsp;[title].[id].[type].html\nTitle is optional and only usefull for the entries in the apache log-files. You may omit both [title] and [type] but if title is present, type must also be there!.\n&nbsp;\n<b>Example:</b>\nTypo3 will interprete this as page with uid=23 and type=1 :\n&nbsp;&nbsp;&nbsp;&nbsp;Startpage.23.1.html\n&nbsp;\nTypo3 will interprete this as the page with alias = &quot;start&quot; and the type is zero (default):\n&nbsp;&nbsp;&nbsp;&nbsp;start.html\n<b>_____________________________________________</b>\n<b>Alternative option (PATH_INFO):</b>\nInstead of using the rewrite-module in apache (eg. if you\'re running Windows!) you can use the PATH_INFO variable from PHP.\nIt\'s very simple. Just set simulateStaticDocuments to &quot;PATH_INFO&quot; and you\'re up and running!\n&nbsp;\n<b>Also:</b> See below, .absRefPrefix\n&nbsp;\n<b>Example (put in Setup-field of your template):</b>\nconfig.simulateStaticDocuments = PATH_INFO";s:7:"default";s:252:"default is defined by a configuration option in localconf.php. It\'s\n$TYPO3_CONF_VARS[&quot;FE&quot;][&quot;simulateStaticDocuments&quot;] = 1;\nThis affects all sites in the database.\nYou can also set this value to the string &nbsp;&quot;PATH_INFO&quot;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:47;a:6:{s:8:"property";s:32:"simulateStaticDocuments_addTitle";s:8:"datatype";s:3:"int";s:11:"description";s:258:"If not zero, Typo3 generates urls with the title in, limited to the first [simulateStaticDocuments_addTitle] number of chars.\n&nbsp;\n<b>Example:</b>\n&nbsp;&nbsp;&nbsp;&nbsp;Startpage.23.1.html\ninstead of the default, &quot;23.1.html&quot;, without the title.";s:7:"default";s:13:"&nbsp;\n&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:48;a:6:{s:8:"property";s:39:"simulateStaticDocuments_noTypeIfNoTitle";s:8:"datatype";s:7:"boolean";s:11:"description";s:403:"If set, then the type-value will not be set in the simulated filename if the type value is zero anyways. However the filename must be without a title.\n&nbsp;\n<b>Example:</b>\n&quot;Startpage.23.0.html&quot; would <i>still </i>be &quot;Startpage.23.0.html&quot;\n&quot;23.0.html&quot; would be &quot;23.html&quot; (that is without the zero)\n&quot;23.1.html&quot; would <i>still</i> be &quot;23.1.html&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:49;a:6:{s:8:"property";s:49:"simulateStaticDocuments_dontRedirectPathInfoError";s:8:"datatype";s:7:"boolean";s:11:"description";s:309:"Regarding PATH_INFO mode:\nWhen a page is requested by &quot;PATH_INFO&quot; method it must be configured in order to work properly. If PATH_INFO is not configured, the index_ts.php script sends a location header to the correct page. However if you better like an error message outputted, just set this option.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:50;a:6:{s:8:"property";s:28:"simulateStaticDocuments_pEnc";s:8:"datatype";s:6:"string";s:11:"description";s:1815:"Allows you to also encode additional parameters into the simulated filename.\n&nbsp;\n<b>Example: </b>\nYou have a news-plugin. The main page has the url &quot;Page_1.228.0.html&quot; but when one clicks on a news item the url will be &quot;Page_1.228.0.html?&amp;tx_mininews_pi1[showUid]=2&amp;cHash=b8d239c224&quot; instead.\nNow, this URL will not be indexed by external search-engines because of the query-string (everything after the &quot;?&quot; mark). This property avoids this problem by encoding the parameters. These are the options:\n&nbsp;\n<b>Value set to &quot;base64&quot;:</b>\nThis will transform the filename used to this value: &quot;Page_1.228+B6JnR4X21pbmluZXdzX3BpMVtzaG93VWlkXT0yJmNIYXNoPWI4ZDIzOWMyMjQ_.0.html&quot;. The querystring has simply been base64-encoded (and some more...) and added to the HTML-filename (so now external search-engines will find this!). The really great thing about this that the filename is self-reliant because the filename contains the parameters. The downside to it is the very very long filename.\n&nbsp;\n<b>Value set to &quot;md5&quot;:</b>\nThis will transform the filename used to this value:\n&quot;Page_1.228+M57867201f4a.0.html&quot;. Now, what a lovely, short filename! Now all the parameters has been hashed into a 10-char string inserted into the filename. At the same time an entry has been added to a cache table in the database so when a request for this filename reaches the frontend, then the REAL parameter string is found in the database! The really great thing about this is that the filename is very short (opposite to the base64-method). The downside to this is that IF you clear the database cache table at any time, the URL here does NOT work until a page with the link has been generated again (re-inserting the parameter list into the database).";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:51;a:6:{s:8:"property";s:35:"content_from_pid_allowOutsideDomain";s:8:"datatype";s:7:"boolean";s:11:"description";s:204:"Using the &quot;Show content from this page instead&quot; feature allows you to insert content from the current domain only. Setting this option will allow content included from anywhere in the page tree!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:52;a:6:{s:8:"property";s:12:"absRefPrefix";s:8:"datatype";s:6:"string";s:11:"description";s:662:"If this value is set, then all relative links in TypoScript are prepended with this string. Used to convert relative paths to absolute paths.\n<b>&nbsp;</b>\n<b>Note:</b> This values is automatically set to the dirname of the index.php script in case simulateStaticDocuments is set to &quot;PATH_INFO&quot;.\nIf you\'re working on a server where you have both internal and external access, you might to yourself a favour and set the absRefPrefix to the url and path of you site, eg. http://www.typo3.com/. If you do not, you risk to render pages to cache from the internal network and thereby prefix image-references and links with a non-accesible path from outside.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:53;a:6:{s:8:"property";s:11:"noPageTitle";s:8:"datatype";s:7:"boolean";s:11:"description";s:75:"If set, the page title will not be printet as the &lt;TITLE&gt; of the doc.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:54;a:6:{s:8:"property";s:14:"pageTitleFirst";s:8:"datatype";s:7:"boolean";s:11:"description";s:101:"If set (and the page title is printed) then the page-title will be printed BEFORE the template title.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:55;a:6:{s:8:"property";s:16:"titleTagFunction";s:8:"datatype";s:13:"function-name";s:11:"description";s:106:"Passes the default &lt;title&gt;-tag content to this function. No typoScript parameters are passed though.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:56;a:6:{s:8:"property";s:13:"headerComment";s:8:"datatype";s:6:"string";s:11:"description";s:203:"The content is added before the &quot;Typo3 Content Management Framework&quot; comment in the &lt;head&gt; section of the page. Use this to insert a note like that &quot;Programmed by My-Agency&quot; ...";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:57;a:6:{s:8:"property";s:8:"language";s:8:"datatype";s:6:"string";s:11:"description";s:356:"Language key. See stdWrap.lang for more information.\nSelect between:\nEnglish &nbsp;(default) = [empty]\nDanish = dk\nGerman = de\nNorwegian = no\nItalian = it\n... (use the TLD names for the country. Must correspond with the key used for backend system language if there is one. If not, use TLD (because if some day this language is added it will get that key))";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:58;a:6:{s:8:"property";s:16:"sys_language_uid";s:8:"datatype";s:3:"int";s:11:"description";s:223:"This value points to the uid of a record from the &quot;sys_language&quot; table and if set, this means that various parts of the frontend display code will select records which is assigned to this language. See -&gt;SELECT";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:59;a:6:{s:8:"property";s:21:"typolinkCheckRootline";s:8:"datatype";s:7:"boolean";s:11:"description";s:398:"If set, then every &quot;typolink&quot; is checked whether it\'s linking to a page within the current rootline of the site.\nIf not, then Typo3 searches for the first found domain record (without redirect) in that rootline from out to in.\nIf found (another domain), then that domain is prepended the link, the external target is used instead and thus the link jumps to the page in the correct domain.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:60;a:6:{s:8:"property";s:23:"insertDmailerBoundaries";s:8:"datatype";s:7:"boolean";s:11:"description";s:235:"If set, boundary marks will be set around all records inserted on the page with cObjects CONTENT and RECORD. They are inserted as HTML-comments and do no harm.\nUsed by the Direct Mail module in Typo3 to segmentize a page by categories.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:61;a:6:{s:8:"property";s:26:"notification_email_urlmode";s:8:"datatype";s:6:"string";s:11:"description";s:593:"This option allows you to handle URL\'s in plain text emails so long URLS of more than 76 chars are not broken. This option can be either empty or &quot;76&quot; or &quot;all&quot;.\nIf the string is blank, all links in plaintext emails are untouched.\nIf it\'s set to 76 then all links longer then 76 characters are stored in the database and a hash is sent in the GET-var ?RDCT=[md5/20] to the index.php script which finds the proper link in the database and issues a location header (redirection).\nIf the value is &quot;all&quot; then ALL &quot;http://&quot; links in the message are converted.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:62;a:6:{s:8:"property";s:27:"notification_email_encoding";s:8:"datatype";s:6:"string";s:11:"description";s:277:"This sets the encoding of plaintext emails (notification messages). Normally no encoding is applied at all. But setting this to eg. &quot;base64&quot; will encode the content with base64 encoding (charset ISO-8859-1).\n&nbsp;\n<b>Values possible:</b>\nbase64\nquoted-printable\n8bit";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:63;a:6:{s:8:"property";s:26:"notification_email_charset";s:8:"datatype";s:6:"string";s:11:"description";s:91:"Alternative charset for the notification mails. Is set only if an encoding type is entered.";s:7:"default";s:10:"ISO-8859-1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:64;a:6:{s:8:"property";s:8:"admPanel";s:8:"datatype";s:34:"boolean / -&gt;ADMPANEL properties";s:11:"description";s:237:"If set, the admin panel appears in the bottom of pages.\n&nbsp;\n<b>NOTE:</b> In addition the panel must be enabled for the user as well, using the TSconfig for the user! See adminguide documentation.\n&nbsp;\n<b>SEE:</b> Admin Panel section";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:65;a:6:{s:8:"property";s:17:"beLoginLinkIPList";s:8:"datatype";s:11:"[IP-number]";s:11:"description";s:303:"If set and REMOTE_ADDR matches one of the listed IP-numbers (Wild-card, *, allowed) then a link to the typo3/ login scrip with redirect pointing back to the page is shown.\n&nbsp;\n<b>NOTE:</b> beLoginLinkIPList_login and/or beLoginLinkIPList_logout (see below) must be defined if the link should show up!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:66;a:6:{s:8:"property";s:23:"beLoginLinkIPList_login";s:8:"datatype";s:4:"HTML";s:11:"description";s:121:"HTML code wrapped with the login link, see \'beLoginLinkIPList\'\n&nbsp;\n<b>Example:</b>\n&lt;HR&gt;&lt;B&gt;LOGING&lt;/B&gt;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:67;a:6:{s:8:"property";s:24:"beLoginLinkIPList_logout";s:8:"datatype";s:4:"HTML";s:11:"description";s:49:"HTML code wrapped with the logout link, see above";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:68;a:6:{s:8:"property";s:12:"index_enable";s:8:"datatype";s:7:"boolean";s:11:"description";s:35:"Enables cached pages to be indexed.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:69;a:6:{s:8:"property";s:15:"index_externals";s:8:"datatype";s:7:"boolean";s:11:"description";s:65:"If set, external media linked to on the pages is indexed as well.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:70;a:6:{s:8:"property";s:14:"index_descrLgd";s:8:"datatype";s:3:"int";s:11:"description";s:124:"This indicates how many chars to preserve as description for an indexed page. This may be used in the search result display.";s:7:"default";s:3:"200";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:71;a:6:{s:8:"property";s:14:"xhtml_cleaning";s:8:"datatype";s:6:"string";s:11:"description";s:1662:"Tries to clean up the output to make it XHTML compliant and a bit more. THIS IS NOT COMPLETE YET, but a &quot;pilot&quot; to see if it makes sense anyways. For now this is what is done:\n&nbsp;\n<b>What it does at this point:</b>\n- All tags (img,br,hr) is ended with &quot;/&gt;&quot; - others?\n- Lowercase for elements and attributes\n- All attributes in quotes\n- Add &quot;alt&quot; attribute to img-tags if it\'s not there already.\n&nbsp;\n<b>What it does NOT do (yet) according to XHTML specs.:</b>\n- Wellformedness: Nesting is NOT checked\n- name/id attribute issue is not observed at this point.\n- Certain nesting of elements not allowed. Most interesting, &lt;PRE&gt; cannot contain img, big,small,sub,sup ...\n- Wrapping scripts and style element contents in CDATA - or alternatively they should have entitites converted.\n- Setting charsets may put some special requirements on both XML declaration/ meta-http-equiv. (C.9)\n- UTF-8 encoding is in fact expected by XML!!\n- stylesheet element and attribute names are NOT converted to lowercase\n- ampersands (and entities in general I think) MUST be converted to an entity reference! (&amp;amps;). This may mean further conversion of non-tag content before output to page. May be related to the charset issue as a whole.\n- Minimized values not allowed: Must do this: selected=&quot;selected&quot;\n&nbsp;\nPlease see the class t3lib_parsehtml for details.\nYou can enable this function by the following values:\n&nbsp;\n<b>all</b> = the content is always processed before it may be stored in cache.\n<b>cached</b> = only if the page is put into the cache,\n<b>output</b> = only the output code just before it\'s echoed out.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('172', '270', '8c378f6afba87a78a9862f799665489f', '', 'tsref:-&gt;addParams', 'a:1:{s:4:"rows";a:2:{i:0;a:6:{s:8:"property";s:7:"_offset";s:8:"datatype";s:3:"int";s:11:"description";s:140:"Use this to define which tag you want to manipulate.\n1 is the first tag in the input, 2 is the second, -1 is the last, -2 is the second last";s:7:"default";s:1:"1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:18:"(array of strings)";s:8:"datatype";s:15:"string /stdWrap";s:11:"description";s:277:"This defines the content of each added property to the tag.\nIf there is a tag-property with this name already (case-sensitive!) that property will be overridden!\nIf the returned value is a blank string (but not zero!) then the existing (if any) property will not be overridden.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('173', '270', '3adc736a6902eab935d222f6dd812cd2', '', 'tsref:-&gt;filelink', 'a:1:{s:4:"rows";a:15:{i:0;a:6:{s:8:"property";s:4:"path";s:8:"datatype";s:13:"path /stdWrap";s:11:"description";s:42:"<b>Example:</b>\n&quot;uploads/media/&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:4:"icon";s:8:"datatype";s:16:"boolean /stdWrap";s:11:"description";s:27:"Set if icon should be shown";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:19:"icon_image_ext_list";s:8:"datatype";s:30:"<i>list</i> of imageextensions";s:11:"description";s:74:"This is the extensions that should render as thumbsnails instead of icons.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:11:"iconCObject";s:8:"datatype";s:7:"cObject";s:11:"description";s:156:"Enter a cObject to use alternatively for the icons, eg. IMAGE type.\nIf this is set, it\'ll substitute the use of the thumbs-script for display of thumbnails.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:9:"icon_link";s:8:"datatype";s:7:"boolean";s:11:"description";s:33:"If the icon should be linked also";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:12:"labelStdWrap";s:8:"datatype";s:13:"-&gt; stdWrap";s:11:"description";s:174:"stdWrap options for the label (by default the label is the filename) before being wrapped with the A-tags.\nUse this to eg. import another label from a database field or such.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:4:"wrap";s:8:"datatype";s:4:"wrap";s:11:"description";s:16:"Wraps the links.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:14:"ATagBeforeWrap";s:8:"datatype";s:7:"boolean";s:11:"description";s:91:"If set, the link is first wrapped with &quot;<i>.wrap&quot;</i> and then the &lt;A&gt;-tag.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:4:"file";s:8:"datatype";s:13:"-&gt; stdWrap";s:11:"description";s:97:"stdWrap of the label (by default the label is the filename) after having been wrapped with A-tag!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:4:"size";s:8:"datatype";s:16:"boolean /stdWrap";s:11:"description";s:27:"Set if size should be shown";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:7:"jumpurl";s:8:"datatype";s:7:"boolean";s:11:"description";s:930:"Decides if the link should call the script with the jumpurl paramter in order to register any clicks in the stat.\nThis has the advantage that any clicks on the file will register in the stat.\nThe disadvantage is, that users cant right-click and select &quot;Save Target As&quot; in the browser.\n<b>&nbsp;</b>\n<b>Properties:</b>\n.secure (boolean)&nbsp;&nbsp;&nbsp;&nbsp;If set, then the file pointed to by jumpurl is NOT redirected to, but rather it\'s read from the file and returned with a correct header. This option adds a hash and locationData to the url and there MUST be access to the record in order to download the file. If the fileposition on the server is furthermore secured by a .htaccess file preventing ANY access, you\'ve got secure download here!\n&nbsp;\n.secure.mimeTypes (list of mimetypes, syntax [ext] = [mimetype]\n&nbsp;\n<b>Example:</b>\n.secure = 1\n.secure.mimeTypes = pdf=application/pdf, doc=application/msword";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:6:"target";s:8:"datatype";s:6:"target";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:7:"stdWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:10:"ATagParams";s:8:"datatype";s:16:"&lt;A&gt;-params";s:11:"description";s:68:"Additional parameters\n&nbsp;\n<b>Example:</b>\nclass=&quot;board&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:22:"removePrependedNumbers";s:8:"datatype";s:7:"boolean";s:11:"description";s:86:"if set, any 2-digit prepended numbers (&quot;eg _23&quot;) in the filename is removed.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('219', '270', '0c841b085963d53a8b0505f67f56947a', '', 'tsref:-&gt;GIFBUILDER.(GBObj).SHADOW', 'a:1:{s:4:"rows";a:6:{i:0;a:6:{s:8:"property";s:10:"textObjNum";s:8:"datatype";s:7:"pos-int";s:11:"description";s:306:"Must point to the TEXT-object if these shadow-properties are not properties to a TEXT-object directly (&quot;stand-alone-shadow&quot;). Then the shadow needs to know which TEXT-object it should be a shadow of!\nIf - on the other hand - the shadow is a property to a text-object, this property is not needed.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"offset";s:8:"datatype";s:3:"x,y";s:11:"description";s:13:"Shadow offset";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:5:"color";s:8:"datatype";s:12:"GraphicColor";s:11:"description";s:12:"Shadow color";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:4:"blur";s:8:"datatype";s:13:"posint (1-99)";s:11:"description";s:377:"Blurring of the shadow. Above 40 only values of 40,50,60,70,80,90 means something.\n&nbsp;\n<b>NOTE:</b> Unfortunately the blurring capabilities of ImageMagick is not very mature in the version 4.2.9. This is addressed in the later version 5.2.0 where a gaussian blur-function is added. BUT as we do cannot use the latest ImageMagick development yet, this is not utilized so far.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:7:"opacity";s:8:"datatype";s:14:"posint (1-100)";s:11:"description";s:93:"Opacity (transparency^-1)\n100% opacity = 0% transparency). Only active with a value for blur.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:9:"intensity";s:8:"datatype";s:13:"posint(0-100)";s:11:"description";s:148:"How &quot;massive&quot; the shadow is. This value can - if it has a high value combined with a blurred shadow - create a kind of soft-edged outline.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('220', '270', 'df23c051c7a400e3684e6621c67c4bb3', '', 'tsref:-&gt;GIFBUILDER.(GBObj).EMBOSS', 'a:1:{s:4:"rows";a:7:{i:0;a:6:{s:8:"property";s:10:"textObjNum";s:8:"datatype";s:7:"pos-int";s:11:"description";s:306:"Must point to the TEXT-object if these shadow-properties are not properties to a TEXT-object directly (&quot;stand-alone-shadow&quot;). Then the shadow needs to know which TEXT-object it should be a shadow of!\nIf - on the other hand - the shadow is a property to a text-object, this property is not needed.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"offset";s:8:"datatype";s:3:"x,y";s:11:"description";s:20:"Offset of the emboss";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:9:"highColor";s:8:"datatype";s:12:"GraphicColor";s:11:"description";s:18:"Upper border-color";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:8:"lowColor";s:8:"datatype";s:12:"GraphicColor";s:11:"description";s:18:"lower border-color";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:4:"blur";s:8:"datatype";s:13:"posint (1-99)";s:11:"description";s:82:"Blurring of the shadow. Above 40 only values of 40,50,60,70,80,90 means something.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:7:"opacity";s:8:"datatype";s:14:"posint (1-100)";s:11:"description";s:93:"Opacity (transparency^-1)\n100% opacity = 0% transparency). Only active with a value for blur.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:9:"intensity";s:8:"datatype";s:13:"posint(0-100)";s:11:"description";s:148:"How &quot;massive&quot; the emboss is. This value can - if it has a high value combined with a blurred shadow - create a kind of soft-edged outline.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('221', '270', '334d3979860c032649bcef2411606021', '', 'tsref:-&gt;GIFBUILDER.(GBObj).OUTLINE', 'a:1:{s:4:"rows";a:3:{i:0;a:6:{s:8:"property";s:10:"textObjNum";s:8:"datatype";s:7:"pos-int";s:11:"description";s:306:"Must point to the TEXT-object if these shadow-properties are not properties to a TEXT-object directly (&quot;stand-alone-shadow&quot;). Then the shadow needs to know which TEXT-object it should be a shadow of!\nIf - on the other hand - the shadow is a property to a text-object, this property is not needed.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:9:"thickness";s:8:"datatype";s:3:"x,y";s:11:"description";s:38:"Thickness in each direction, range 1-2";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:5:"color";s:8:"datatype";s:12:"GraphicColor";s:11:"description";s:13:"Outline color";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('222', '270', 'e3f360fc870a505831c8128a3ee4db42', '', 'tsref:-&gt;GIFBUILDER.(GBObj).BOX', 'a:1:{s:4:"rows";a:3:{i:0;a:6:{s:8:"property";s:10:"dimensions";s:8:"datatype";s:13:"x,y,w,h +calc";s:11:"description";s:156:"Dimensions of a filled box.\nx,y &nbsp;&nbsp;&nbsp;is the offset.\nw,h &nbsp;&nbsp;&nbsp;is the dimensions. Dimensions of 1 will result in 1-pixel wide lines!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:5:"color";s:8:"datatype";s:12:"GraphicColor";s:11:"description";s:10:"fill-color";s:7:"default";s:5:"black";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:5:"align";s:8:"datatype";s:7:"VHalign";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('223', '270', '8f9bf9252b10ca4263fb8dc895ce2cb7', '', 'tsref:-&gt;GIFBUILDER.(GBObj).IMAGE', 'a:1:{s:4:"rows";a:5:{i:0;a:6:{s:8:"property";s:4:"file";s:8:"datatype";s:11:"imgResource";s:11:"description";s:13:"The imagefile";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"offset";s:8:"datatype";s:9:"x,y +calc";s:11:"description";s:6:"Offset";s:7:"default";s:3:"0,0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:4:"tile";s:8:"datatype";s:3:"x,y";s:11:"description";s:89:"tile x,y times.\nMaximum times is 20 each direction. If you need more, use a larger image.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:5:"align";s:8:"datatype";s:7:"VHalign";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:4:"mask";s:8:"datatype";s:11:"imgResource";s:11:"description";s:38:"Optional mask-image for the imagefile.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
