INSERT INTO static_tsconfig_help VALUES ('229', '270', '1f386c921c1bc572792e6f3de9c9dec6', '', 'tsref:-&gt;IMGMAP', 'a:1:{s:4:"rows";a:4:{i:0;a:6:{s:8:"property";s:3:"url";s:8:"datatype";s:3:"url";s:11:"description";s:11:"url to link";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"target";s:8:"datatype";s:6:"target";s:11:"description";s:15:"target for link";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:6:"noBlur";s:8:"datatype";s:7:"Boolean";s:11:"description";s:188:"Normally graphical links are &quot;blurred&quot; if the browser is msie. This removes the ugly box around a link.\nIf this property is set, the link is NOT blurred with &quot;onFocus&quot;.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:7:"explode";s:8:"datatype";s:3:"x,y";s:11:"description";s:146:"This &quot;explodes&quot; or &quot;implodes&quot; the image-map. Useful to let the hot area cover a little more than just the letters of the text.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('230', '270', '5c576ef57f313263731885ee7eb93387', '', 'tsref:(cObject).HMENU.(mObj)', 'a:1:{s:4:"rows";a:9:{i:0;a:6:{s:8:"property";s:12:"sectionIndex";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:11:"(see below)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:23:"alternativeSortingField";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:489:"Normally the menuitems are sorted by the fields &quot;sorting&quot; in the pages- and tt_content-table. Here you can enter a list of fields that is used in the SQL- &quot;ORDER BY&quot; statement instead.\n&nbsp;\n<b>Examples (for &quot;pages&quot; table):</b>\nalternativeSortingField = title desc\n(This will render the menu in reversed alphabetical order)\n&nbsp;\n<b>LIMITATIONS:</b>\nThis property works with normal menus, sectionsIndex menus and special-menus of type &quot;directory&quot;.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:8:"JSWindow";s:8:"datatype";s:7:"boolean";s:11:"description";s:401:"If set, the links of the menu-items will open by JavaScript in a pop-up window.\n&nbsp;\n<b>.newWindow boolean, that lets every menuitem open in its own window opposite to opening in the same window for each click.</b>\n&nbsp;\n<b>.params</b> is the list of parameters sent to the JavaScript open-window function, eg:\nwidth=200,height=300,status=0,menubar=0\n&nbsp;\n<b>NOTE:</b> Does not work with JSMENU\'s";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:13:"imgNamePrefix";s:8:"datatype";s:6:"string";s:11:"description";s:76:"prefix for the imagenames. This prefix is appended with the uid of the page.";s:7:"default";s:15:"&quot;img&quot;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:16:"imgNameNotRandom";s:8:"datatype";s:7:"boolean";s:11:"description";s:346:"If set, the image names of menuitems is not randomly assigned. Usefull switch if you\'re manipulating these images with some external JavaScript\n&nbsp;\n<b>NOTE:</b> Don\'t set this if you\'re working with a menu with sectionIndex! In that case you need special unique names of items based on something else than the uid of the parent page of course!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:13:"debugItemConf";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:157:"Outputs (by the debug()-function) the configuration arrays for each menuitem. Useful to debug optionSplit things and such...\nApplies to GMENU, TMENU, IMGMENU";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:10:"overrideId";s:8:"datatype";s:17:"integer (page-id)";s:11:"description";s:387:"If set, then all links in the menu will point to this pageid. Instead the real uid of the page is sent by the parameter &quot;&amp;real_uid=[uid]&quot;.\nThis feature is smart, if you have inserted a menu from somewhere else, perhaps a shared menu, but wants the menuitems to call the same page, which then generates a proper output based on the real_uid.\nApplies to GMENU, TMENU, IMGMENU";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:9:"addParams";s:8:"datatype";s:6:"string";s:11:"description";s:158:"Additional parameter for the menu-links.\n&nbsp;\n<b>Example:</b>\n&quot;&amp;some_var=some%20value&quot;\nMust be rawurlencoded.\nApplies to GMENU, TMENU, IMGMENU";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:17:"itemArrayProcFunc";s:8:"datatype";s:13:"function-name";s:11:"description";s:662:"The first variable passed to this function is the &quot;menuArr&quot; array with the menuitems as they are collected based on the type of menu.\nYou\'re free to manipulate or add to this array as you like. Just remember to return the array again!\n&nbsp;\n<b>Note:</b>\n.parentObj property is <u>hardcoded</u> to be a reference to the calling tslib_menu object. Here you\'ll find eg. -&gt;id to be the uid of the menu item generating a submenu and such.\n&nbsp;\n<b>Presetting element state</b>\nYou can override element states like SPC, IFSUB, ACT, CUR or USR by setting the key ITEM_STATE in the page records. See cObject HMENU/special=userdefined for more information.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('215', '270', '9e45ae1e8e57e33a34339154486c9e40', '', 'tsref:(cObject).MULTIMEDIA', 'a:1:{s:4:"rows";a:2:{i:0;a:6:{s:8:"property";s:4:"file";s:8:"datatype";s:17:"resource /stdWrap";s:11:"description";s:363:"The multimedia file. Types are:\ntxt, html, htm:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Inserted directly\nclass:&nbsp;&nbsp;&nbsp;&nbsp;Java-applet\nswf:&nbsp;&nbsp;&nbsp;&nbsp;Flash animation\nswa, dcr:&nbsp;&nbsp;&nbsp;&nbsp;ShockWave Animation\nwav,au:&nbsp;&nbsp;&nbsp;&nbsp;Sound\navi,mov,asf,mpg,wmv:&nbsp;&nbsp;&nbsp;&nbsp;Movies (AVI, QuickTime, MPEG4)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"params";s:8:"datatype";s:15:"string /stdWrap";s:11:"description";s:314:"This is parameters for the multimedia-objects. Use this to enter stuff like with and height:\n&nbsp;\n<b>Example:</b>\nwidth=200\nheight=300\n&nbsp;\n... will generate a tag like \'&lt;embed .... width=&quot;200&quot; height=&quot;300&quot;&gt;\'\nheight=\n&nbsp;\nAn empty string will remove the parameter from the embed-tag";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('216', '270', '71d45380d5c1507c11c46fa7b95d72fa', '', 'tsref:(cObject).EDITPANEL', 'a:1:{s:4:"rows";a:7:{i:0;a:6:{s:8:"property";s:5:"label";s:8:"datatype";s:6:"string";s:11:"description";s:114:"Title for the panel. You can insert the record title with %s\n&nbsp;\n<b>Example:</b>\nSection: &lt;B&gt;%s&lt;/B&gt;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:5:"allow";s:8:"datatype";s:6:"string";s:11:"description";s:346:"Define which functions are accessible. Further this list may be reduced, if the BE_USER does not have permission to perform the action\nValues should be listed separated by comma. This is the options you can choose between:\ntoolbar,edit,new,delete,move,hide\n(toolbar is a general list of icons regarding the page, so use this for pagerecords only)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:18:"newRecordFromTable";s:8:"datatype";s:17:"string, tablename";s:11:"description";s:98:"Will display a panel for creation of new element (in the top of list) on the page from that table.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:4:"line";s:8:"datatype";s:13:"boolean / int";s:11:"description";s:120:"If set, a black line will appear after the panel. This value will indicate the distance from the black line to the panel";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:18:"edit.displayRecord";s:8:"datatype";s:7:"boolean";s:11:"description";s:65:"If set, then the record edited is display above the editing form.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:14:"onlyCurrentPid";s:8:"datatype";s:7:"boolean";s:11:"description";s:99:"If set, only records with a pid matching the current id (TSFE-&gt;id) will be shown with the panel.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:13:"previewBorder";s:8:"datatype";s:13:"boolean / int";s:11:"description";s:152:"If set, the hidden/starttime/endtime/fe_user elements which are preview will have a border around.\nThe integer value denotes the thickness of the border";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('217', '270', '2b302bcf738e9cbe7f8a008591706947', '', 'tsref:-&gt;GIFBUILDER', 'a:1:{s:4:"rows";a:12:{i:0;a:6:{s:8:"property";s:10:"1,2,3,4...";s:8:"datatype";s:29:"GifBuilderObj\n+ .if (-&gt;if)";s:11:"description";s:211:".if (-&gt;if) &nbsp;is a property of all gifbuilder-objects. If the property is present and NOT set, the object is NOT rendered! This corresponds to the functionallity of &quot;.if&quot; of the stdWrap-function.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:2:"XY";s:8:"datatype";s:9:"x,y +calc";s:11:"description";s:21:"Size of the gif-file.";s:7:"default";s:6:"100,20";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:6:"format";s:8:"datatype";s:33:"&quot;gif&quot; / &quot;jpg&quot;";s:11:"description";s:57:"Output type.\n&quot;jpg&quot;/&quot;jpeg&quot; = jpg-image";s:7:"default";s:3:"gif";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:12:"reduceColors";s:8:"datatype";s:14:"posint (1-255)";s:11:"description";s:41:"Reduce the number of colors (if gif-file)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:21:"transparentBackground";s:8:"datatype";s:7:"boolean";s:11:"description";s:284:"Set this flag to render the background transparent. Typo3 makes the color found at position 0,0 of the image (upper left corner) transparent.\nIf you render text you should leave the niceText option OFF as the result with probably be more precise without the niceText antialiasing hack";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:16:"transparentColor";s:8:"datatype";s:18:"HTMLColor /stdWrap";s:11:"description";s:405:"Specify a color that should be transparent\n&nbsp;\n<b>Example-values:</b>\n#ffffcc\nred\n255,255,127\n&nbsp;\n<b>Option:</b>\ntransparentColor.closest = 1\nThis will allow for the closest color to be matched instead. You may need this if you image is not garanteed &quot;clean&quot;.\n&nbsp;\n<b>NOTE:</b> You may experience that this doesn\'t work if you use reduceColors-option or render text with niceText-option.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:7:"quality";s:8:"datatype";s:15:"posint (10-100)";s:11:"description";s:47:"JPG-quality (if &quot;.format&quot; = jpg/jpeg)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:9:"backColor";s:8:"datatype";s:21:"GraphicColor\n+stdWrap";s:11:"description";s:28:"Background color for the gif";s:7:"default";s:5:"white";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:6:"offset";s:8:"datatype";s:9:"x,y +calc";s:11:"description";s:30:"Offset all objects on the gif.";s:7:"default";s:3:"0,0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:8:"workArea";s:8:"datatype";s:14:"x,y,w,h + calc";s:11:"description";s:248:"Define the workarea on the giffile. All the GifBuilderObj\'s will see this as the dimensions of the gif-file regarding alignment, overlaying of images an so on. Only will TEXT-objects exceeding the boundaries of the workarea print outside this area.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:8:"maxWidth";s:8:"datatype";s:6:"pixels";s:11:"description";s:25:"Maximal width of gif-file";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:9:"maxHeight";s:8:"datatype";s:6:"pixels";s:11:"description";s:26:"Maximal heigth of gif-file";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('218', '270', '5093dac38c2f3f7e3cc38d85a4ccece0', '', 'tsref:-&gt;GIFBUILDER.(GBObj).TEXT', 'a:1:{s:4:"rows";a:21:{i:0;a:6:{s:8:"property";s:4:"text";s:8:"datatype";s:7:"stdWrap";s:11:"description";s:217:"This is text text-string on the gif-file. The item is rendered only is this string is not empty.\nThe cObj-&gt;data-array is loaded with the page-record, if for example the GIFBUILDER-object is used by GMENU or IMGMENU";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:13:"textMaxLength";s:8:"datatype";s:3:"int";s:11:"description";s:121:"The maximum length of the text. &nbsp;This is just a natural break that prevents incidental rendering of very long texts!";s:7:"default";s:3:"100";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:8:"maxWidth";s:8:"datatype";s:6:"pixels";s:11:"description";s:161:"Sets the maximum width in pixels, the text must be. Reduces the fontSize if the text does not fit within this width.\n(By Rene Fritz &lt;r.fritz@colorcube.de&gt;)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:14:"doNotStripHTML";s:8:"datatype";s:7:"boolean";s:11:"description";s:104:"If set, HTML-tags in the string inserted are NOT removed. Any other way HTML-code is removed by default!";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:8:"fontSize";s:8:"datatype";s:6:"posint";s:11:"description";s:9:"Font size";s:7:"default";s:2:"12";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:9:"fontColor";s:8:"datatype";s:12:"GraphicColor";s:11:"description";s:10:"Font color";s:7:"default";s:5:"black";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:8:"fontFile";s:8:"datatype";s:8:"resource";s:11:"description";s:42:"Font face (truetype font you can upload!!)";s:7:"default";s:5:"Arial";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:5:"angle";s:8:"datatype";s:6:"degree";s:11:"description";s:180:"Rotation degrees of the text.\n&nbsp;\n<b>NOTE:</b> This value will malfunction with the &quot;.spacing&quot;-property set at the same time. Then each letter will be rotated instead!";s:7:"default";s:19:"0\nRange: -90 til 90";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:5:"align";s:8:"datatype";s:5:"align";s:11:"description";s:21:"Alignment of the text";s:7:"default";s:4:"left";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:6:"offset";s:8:"datatype";s:9:"x,y +calc";s:11:"description";s:18:"Offset of the text";s:7:"default";s:3:"0,0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:9:"antiAlias";s:8:"datatype";s:7:"Boolean";s:11:"description";s:139:"FreeType antialiasing. Notice, the default mode is &quot;on&quot;!\n&nbsp;\n<b>Note:</b> This option is not available if .niceText is enabled";s:7:"default";s:1:"1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:10:"iterations";s:8:"datatype";s:6:"posint";s:11:"description";s:179:"How many times the text should be &quot;printed&quot; onto it self. This will add the effect of bold text.\n&nbsp;\n<b>Note:</b> This option is not available if .niceText is enabled";s:7:"default";s:1:"1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:7:"spacing";s:8:"datatype";s:6:"posint";s:11:"description";s:53:"Pixel-distance between letters. This may render ugly!";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:11:"wordSpacing";s:8:"datatype";s:6:"posint";s:11:"description";s:29:"Pixel-distance between words.";s:7:"default";s:24:"= &quot;.spacing&quot;*2";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:4:"hide";s:8:"datatype";s:7:"boolean";s:11:"description";s:159:"If this is true, the text is NOT printed.\nThis feature may be used if you need a shadow-object to base a shadow on the text, but do not want the text to print.";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:15;a:6:{s:8:"property";s:16:"hideButCreateMap";s:8:"datatype";s:7:"boolean";s:11:"description";s:225:"If this option is set, the text will not be rendered. Shadows and emboss will, though, so don\'t apply these!! But this feature is also meant only to enable a text to generate the imageMap coordinates without rendering itself.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:16;a:6:{s:8:"property";s:6:"emboss";s:8:"datatype";s:24:"GifBuilderObj-&gt;EMBOSS";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:17;a:6:{s:8:"property";s:6:"shadow";s:8:"datatype";s:24:"GifBuilderObj-&gt;SHADOW";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:18;a:6:{s:8:"property";s:7:"outline";s:8:"datatype";s:25:"GifBuilderObj-&gt;OUTLINE";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:19;a:6:{s:8:"property";s:6:"imgMap";s:8:"datatype";s:11:"-&gt;IMGMAP";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:20;a:6:{s:8:"property";s:8:"niceText";s:8:"datatype";s:7:"boolean";s:11:"description";s:987:"This is a very popular feature that helps to render small letters much nicer than the freetype library can normally do. But it also loads the system very much!\nThe principle of this function is to create a black/white giffile in twice or more times the size of the actual gif-file and then print the text onto this is a scaled dimension. Afterwards ImageMagick (IM) scales down the mask and masks the font color down on the original gif-file through the temporary mask.\nThe fact that the font &nbsp;is &nbsp;actually rendered in the double size and scaled down adds a more homogenous shape to the lettes. Some fonts are more critical than others though. &nbsp;If you do not need the quality, then don\'t use the function.\n&nbsp;\n<b>Some properties:</b>\n.before = IM-params before scale\n.after = IM-params after scale\n.sharpen = sharpen-value for the mask (after scaling), integer 0-99 (this enables you to make the text crisper if it\'s too blurred!)\n.scaleFactor = scaling-factor, int 2-5";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('209', '270', '06d8abccf0aba6b413150e9c100f4bd4', '', 'tsref:(cObject).SEARCHRESULT', 'a:1:{s:4:"rows";a:14:{i:0;a:6:{s:8:"property";s:11:"allowedCols";s:8:"datatype";s:6:"string";s:11:"description";s:111:"List (separated by &quot;:&quot;) of allowed table-cols.\n&nbsp;\n<b>Example:</b>\npages.title:tt_content.bodytext";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:6:"layout";s:8:"datatype";s:6:"string";s:11:"description";s:551:"This defines how the search content is shown.\n&nbsp;\n<b>Example:</b>\nThis substitutes the following fields:\n###RANGELOW###:&nbsp;&nbsp;&nbsp;&nbsp;The low result range, eg. &quot;1&quot;\n###RANGEHIGH###:&nbsp;&nbsp;&nbsp;&nbsp;The high result range, eg. &quot;10&quot;\n###TOTAL###:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The total results\n###RESULT###:&nbsp;&nbsp;&nbsp;&nbsp;The result itself\n###NEXT###:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The next-button\n###PREV###:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;The prev-button";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:4:"next";s:8:"datatype";s:7:"cObject";s:11:"description";s:126:"This cObject will be wrapped by a link to the next searchresult. This is the code substituting the &quot;###NEXT###&quot;-mark";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:4:"prev";s:8:"datatype";s:7:"cObject";s:11:"description";s:126:"This cObject will be wrapped by a link to the prev searchresult. This is the code substituting the &quot;###PREV###&quot;-mark";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:6:"target";s:8:"datatype";s:6:"target";s:11:"description";s:27:"target til next/prev links!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:5:"range";s:8:"datatype";s:3:"int";s:11:"description";s:32:"The number of results at a time!";s:7:"default";s:2:"20";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:9:"renderObj";s:8:"datatype";s:7:"cObject";s:11:"description";s:331:"the cObject to render the searchresults\n$cObj-&gt;data array is set to the resulting record from the search.\nPlease note, that in all fields are named [tablename]_[fieldnam]. Thus the pagetitle is in the field &quot;pages_title&quot;.\nApart from this, these fields from the pages-table are also present:\n&nbsp;&nbsp;&nbsp;&nbsp;uid";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:10:"renderWrap";s:8:"datatype";s:4:"wrap";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:9:"resultObj";s:8:"datatype";s:7:"cObject";s:11:"description";s:56:"the cObject prepended in the search results returns rows";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:11:"noResultObj";s:8:"datatype";s:7:"cObject";s:11:"description";s:50:"the cObject used if the search results in no rows.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:9:"noOrderBy";s:8:"datatype";s:7:"boolean";s:11:"description";s:87:"If this is set, the result is NOT sorted after lastUpdated, tstamp for the pages-table.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:4:"wrap";s:8:"datatype";s:4:"wrap";s:11:"description";s:25:"Wrap the whole content...";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:7:"stdWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:25:"Wrap the whole content...";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:22:"addExtUrlsAndShortCuts";s:8:"datatype";s:7:"boolean";s:11:"description";s:405:"If set, then the doktypes 3 and 4 (External URLS and Shortcuts) are added to the doktypes being searched.\nHowever at this point in time, no pages will be select if they do not have at least one tt_content record on them! That is because the pages and tt_content (or other) table is joined. So there must at least one occurance of a tt_content element on a External URL / Shortcut page for them to show up.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('210', '270', '4a24c3602d0800d7feacc6759c6dbf21', '', 'tsref:(cObject).USER/(cObject).USER_INT', 'a:1:{s:4:"rows";a:2:{i:0;a:6:{s:8:"property";s:8:"userFunc";s:8:"datatype";s:13:"function-name";s:11:"description";s:784:"The name of the function. If you specify the name with a \'-&gt;\' in, it\'s intepreted as a call to a method in a class.\nTwo parameters are sent: A content variable (which is empty in this case, but not when used from stdWrap function .postUserFunc and .preUserFunc) and the second parameter is an array with the properties of this cObject if any.\n&nbsp;\n<b>Example:</b>\nThis TypoScript will display all content element headers of a page in reversed order. Please take a look in media/scripts/example_callfunction.php!!\n(Also demonstrated on the testsite, page \n&nbsp;\npage = PAGE\npage.typeNum=0\nincludeLibs.something = media/scripts/example_callfunction.php\n&nbsp;\npage.30 = USER\npage.30 {\n&nbsp;&nbsp;userFunc = user_various-&gt;listContentRecordsOnPage\n&nbsp;&nbsp;reverseOrder = 1\n}";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:11:"includeLibs";s:8:"datatype";s:23:"<i>list of </i>resource";s:11:"description";s:361:"<b>(This property applies only if the object is created as USER_INT)</b>\nThis is a comma-separated list of resources that are included as PHP-scripts (with include_once() function) if this script is included.\nThis is possible to do because any include-files will be known before the scripts are included. That\'s not the case with the regular PHP_SCRIPT cObject.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('211', '270', '58679d0408b0f09f69cb93e0634e69c9', '', 'tsref:(cObject).PHP_SCRIPT', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:4:"file";s:8:"datatype";s:8:"resource";s:11:"description";s:910:"File that will be included. This file must be valid PHP-code! It\'s included with &quot;include()&quot;;\n&nbsp;\n<b>Directions:</b>\n<b>1) All content must be put into $content.</b> No output must be echo\'ed out!\n&nbsp;\n2) Call $GLOBALS[&quot;TSFE&quot;]-&gt;set_no_cache(), if you want to disable caching of the page. Set this during development! And set it, if the content you create may not be cached.\n&nbsp;\n<b>NOTE:</b> If you have a parsing error in your include script the $GLOBALS[&quot;TSFE&quot;]-&gt;set_no_cache() function is NOT executed and thereby does not disable caching. Upon a parse-error you must manually clear the page-cache after you have corrected your error!\n3) the array $conf contains the configuration for the PHP_SCRIPT cObject. Try debug($conf) to see the content printed out for debugging!\n<i>See later in this manual for an introduction to writing your own PHP include-scripts.</i>";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('212', '270', '6307a490ae08c78efdc8a3ceb92bfde6', '', 'tsref:(cObject).PHP_SCRIPT_INT', 'a:1:{s:4:"rows";a:2:{i:0;a:6:{s:8:"property";s:4:"file";s:8:"datatype";s:8:"resource";s:11:"description";s:2045:"File that will be included. This file must be valid PHP-code! It\'s included with &quot;include()&quot;;\n&nbsp;\n<b>Purpose:</b>\nThis basically works like PHP_SCRIPT. But the vital difference is that inserting a PHP_SCRIPT<b>_INT </b>(internal opposed to external, see below) merely inserts a divider-string in the code and then serializes the current cObj and puts it in the $GLOBALS[&quot;TSFE&quot;]-&gt;config[&quot;INTincScript&quot;]-array. This array is saved with the cached page-content.\nNow, the point is, that including a script like this lets you avoid disabling pagecaching. The reason is that the cached page contains the divider string and when a &quot;static&quot; page is fetched from cache, it\'s divided by that string and the dynamic content object is inserted.\nThis is the compromise option of all three PHP_SCRIPT-cObjects, because the page-data is all cached, but still the pagegen.php script is included, which initializes all the classes, objects and so. What you gain here is an environment for your script almost exactly the same as PHP_SCRIPT because your script is called from inside a class tslib_cObj object. You can work with all functions of the tslib_cObj-class. But still all the &quot;static&quot; pagecontent is only generated once, cached and only your script is dynamically rendered.\n&nbsp;\n<b>Rules:</b>\n- calls to $GLOBALS[&quot;TSFE&quot;]-&gt;set_no_cache() and $GLOBALS[&quot;TSFE&quot;]-&gt;set_cache_timeout_default() makes no sense in this situation.\n- parsing errors does not interfere with caching\n- Be aware that certain global variables may not be set as usual and be available as usual when working in this mode. Most scripts should work out-of-the-box with this option though.\n- Dependence and use of LOAD_REGISTER is fragile because the PHP_SCRIPT_INT is not rendered until <i>after</i> the cached content and due to this changed order of events, use of LOAD_REGISTER may not work.\n- You can not nest PHP_SCRIPT_INT and PHP_SCRIPT_EXT in PHP_SCRIPT_INT. You may nest PHP_SCRIPT cObjects though.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:11:"includeLibs";s:8:"datatype";s:23:"<i>list of </i>resource";s:11:"description";s:288:"This is a comma-separated list of resources that are included as PHP-scripts (with include_once() function) if this script is included.\nThis is possible to do because any include-files will be known before the scripts are included. That\'s not the case with the regular PHP_SCRIPT cObject.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('213', '270', 'dede7a11cddf475e1eaf0a9116291157', '', 'tsref:(cObject).PHP_SCRIPT_EXT', 'a:1:{s:4:"rows";a:2:{i:0;a:6:{s:8:"property";s:4:"file";s:8:"datatype";s:8:"resource";s:11:"description";s:1393:"File that will be included. This file must be valid PHP-code! It\'s included with &quot;include()&quot;;\n&nbsp;\n<b>Purpose:</b>\nThis works like PHP_SCRIPT_INT, because a divider string is also inserted in the content for this kind of include-script. But the difference is that the content is divided as the very last thing before it\'s output to the browser.\nThis basically means that PHP_SCRIPT<b>_EXT</b> (external, because it\'s included in the global space in index_ts.php file!!) can output data directly with echo-statements!\nThis is a very &quot;raw&quot; version of PHP_SCRIPT because it\'s not included from inside an object and you have only very few standard functions from Typo3 to call.\nThis is the fastest option of all three PHP_SCRIPT-cObjects, because the page-data is all cached and your dynamic content is generated by a raw php-script\n&nbsp;\n<b>Rules:</b>\n- All content can be either 1) echo\'ed out directly, or 2) returned in $content.\n- calls to $GLOBALS[&quot;TSFE&quot;]-&gt;set_no_cache() and $GLOBALS[&quot;TSFE&quot;]-&gt;set_cache_timeout_default() makes no sense in this situation.\n- parsing errors does not interfere with caching\n- In the global name-space, the array $REC contains the current record when the file was &quot;inserted&quot; on the page, and $CONF-array contains the configuration for the script.\n- Don\'t mess with the global vars named $EXTiS_* &nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:11:"includeLibs";s:8:"datatype";s:23:"<i>list of </i>resource";s:11:"description";s:288:"This is a comma-separated list of resources that are included as PHP-scripts (with include_once() function) if this script is included.\nThis is possible to do because any include-files will be known before the scripts are included. That\'s not the case with the regular PHP_SCRIPT cObject.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('214', '270', '36fc199c62fb88c0a813769e3de9adc9', '', 'tsref:(cObject).TEMPLATE', 'a:1:{s:4:"rows";a:8:{i:0;a:6:{s:8:"property";s:8:"template";s:8:"datatype";s:7:"cObject";s:11:"description";s:78:"This must be loaded with the template-code. If not the object returns nothing.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:8:"subparts";s:8:"datatype";s:27:"<i>Array... of </i>\ncObject";s:11:"description";s:1188:"This is an array of subpart-markers (case-sensitive).\nA subpart is defined by two markers in the template. The markers must be wrapped by &quot;###&quot; on both sides. You may insert the subpart-markers inside HTML-comment-tags!!\n&nbsp;\n<b>Example: </b>\nsubparts {\n&nbsp;&nbsp;HELLO = TEXT\n&nbsp;&nbsp;HELLO.value = En subpart er blevet erstattet!!\n}\n&nbsp;\nIn the templates:\n&lt;!-- &nbsp;start of subpart: ###HELLO### --&gt;\nThis is the HTML.code, that will be loaded in the register and replaced with the result...\n&lt;!-- end ###HELLO### --&gt;\n&nbsp;\n<b>NOTE:</b>\nBefore the content-objects of each subpart is generated, all subparts in the array are extracted and loaded into the register so that you can load them from there later on.\nThe register-key for each subparts code is &quot;SUBPART_[theSubpartkey]&quot;.\nIn addition the current-value is loaded with the content of each subpart just before the cObject for the subpart is parsed. That makes it quite easy to load the subpart of the cObject (eg: &quot;.current=1&quot;)\nEg. this subpart above has the register-key &quot;SUBPART_HELLO&quot;.\n<i>This is valid ONLY if the property .nonCachedSubst is not set! (see below)</i>";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:5:"marks";s:8:"datatype";s:27:"<i>Array... of </i>\ncObject";s:11:"description";s:445:"This is an array of marks-markers (case-sensitive).\nA mark is defined by one markers in the template. The marker must be wrapped by &quot;###&quot; on both sides. Opposite to subparts, you may NOT insert the subpart-markers inside HTML-comment-tags! (They will not be removed).\nMarks are substituted bya str_replace-function. The subparts loaded in the register is available also to the cObjects of markers (only if .nonCachedSubst is not set!).";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:5:"wraps";s:8:"datatype";s:27:"<i>Array... of </i>\ncObject";s:11:"description";s:438:"This is an array of wraps-markers (case-sensitive).\nThis is shown best by an example:\n<b>Example: </b>\nsubparts {\n&nbsp;&nbsp;MYLINK = TEXT\n&nbsp;&nbsp;MYLINK.value = &lt;A href=&quot;#&quot;&gt; | &lt;/A&gt;\n}\n&nbsp;\nIn the template:\nThis is &lt;!--###MYLINK###--&gt;a link to my&lt;!--###MYLINK###--&gt; page!\n&nbsp;\nIn this example the MYLINK subpart will be substituted by the wrap which is the content returned by the MYLINK cObject.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:13:"workOnSubpart";s:8:"datatype";s:6:"string";s:11:"description";s:204:"This is an optional definition of a subpart, that we decide to work on. In other words; if you define this value that subpart is extracted from the template and is the basis for this whole templateobject.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:10:"markerWrap";s:8:"datatype";s:4:"wrap";s:11:"description";s:232:"This is the wrap the markers is wrapped with. The default value is ### | ### resulting in the markers to be presented as ###[marker_key]###.\nAny whitespace around the wrap-items is stripped before they are set around the marker_key.";s:7:"default";s:9:"### | ###";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:20:"substMarksSeparately";s:8:"datatype";s:7:"boolean";s:11:"description";s:424:"If set, then marks are substituted in the content AFTER the substitution of subparts and wraps.\nNormally marks are not substituted inside of subparts and wraps when you are using the default cached mode of the TEMPLATE cObject. That is a problem if you have marks inside of subparts! But setting this flag will make the marker-substitution a non-cached, subsequent process.\nAnother solution is to turn of caching, see below.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:14:"nonCachedSubst";s:8:"datatype";s:7:"boolean";s:11:"description";s:700:"If set, then the substitution mode of this cObject is totally different. Normally the raw template is read and divided into the sections denoted by the marks, subparts and wraps keys. The good thing is high speed, because this &quot;pre-parsed&quot; template is cached. The bad thing is that templates that depends on incremental substition (where the order of substition is important) will not work so well.\nBy setting this flag, markers are first substituted by str_replace in the template - one by one. Then the subparts are substituted one by one. And finally the wraps one by one.\nObviously you loose the ability to refer to other parts in the template with the register-keys as described above.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('208', '270', 'd6df84910bc06faaa29868176f91b324', '', 'tsref:(cObject).FORM', 'a:1:{s:4:"rows";a:29:{i:0;a:6:{s:8:"property";s:4:"data";s:8:"datatype";s:19:"string/-&gt;stdWrap";s:11:"description";s:99:"This is the data that sets up the form. See above.\n&quot;||&quot; can be used instead of linebreaks";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:9:"dataArray";s:8:"datatype";s:31:"<i>[array of form elements]</i>";s:11:"description";s:2596:"This is an alternative way to define the form-fields. Instead of using the syntax with vertical separator bars suggested by the .data property, you can define the elements in regular TypoScript style arrays.\n.dataArray is <i>added </i>to the input in .data if any.\nEvery entry in the dataArray is numeric and has three main properties, <i>label</i>, <i>type</i>, <i>value</i> and <i>required</i>. \'label\' and \'value\' has stdWrap properties. \nThere is an alternative property to .value, which is .valueArray. This is also an array in the same style with numeric entries which has properties <i>label</i>, <i>value</i> and <i>selected</i>. \'label\' has stdWrap properties.\n&nbsp;\n<b>Example:</b>\n&nbsp;&nbsp;dataArray {\n&nbsp;&nbsp;&nbsp;&nbsp;10.label = Name:\n&nbsp;&nbsp;&nbsp;&nbsp;10.type = name=input\n&nbsp;&nbsp;&nbsp;&nbsp;10.value = [Enter name]\n&nbsp;&nbsp;&nbsp;&nbsp;10.required = 1\n&nbsp;&nbsp;&nbsp;&nbsp;20.label = Eyecolor\n&nbsp;&nbsp;&nbsp;&nbsp;20.type = eyecolor=select\n&nbsp;&nbsp;&nbsp;&nbsp;20.valueArray {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.label = Blue\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.value = 1\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.label = Red\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.value = 2\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.selected = 1\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;40.type = submit=submit\n&nbsp;&nbsp;&nbsp;&nbsp;40.value = Submit\n&nbsp;&nbsp;}\n&nbsp;\n&nbsp;\nThis is the same as this line in the .data property:\n&nbsp;\nName: | *name=input | [Enter name]\nEyecolor: | eyecolor=select | Blue=1, *Red=2\n| submit=submit | Submit\n&nbsp;\n<b>Why do it this way?</b> &nbsp;Good question, but doing it this way has a tremendous advantage because labels are all separated from the codes. In addition it\'s much easier to pull out or insert new elements in the form.\nInserting an email-field after the name field would be like this:\n&nbsp;&nbsp;dataArray {\n&nbsp;&nbsp;&nbsp;&nbsp;15.label = Email:\n&nbsp;&nbsp;&nbsp;&nbsp;15.type = input\n&nbsp;&nbsp;&nbsp;&nbsp;15.value = your@email.com\n&nbsp;&nbsp;}\n&nbsp;\nOr translating the form to danish (setting config.language to \'dk\'):\n&nbsp;\n&nbsp;&nbsp;dataArray {\n&nbsp;&nbsp;&nbsp;&nbsp;10.label.lang.dk = Navn:\n&nbsp;&nbsp;&nbsp;&nbsp;10.value.lang.dk = [Indtast dit navn]\n&nbsp;&nbsp;&nbsp;&nbsp;20.label.lang.dk = &Oslash;jenfarve\n&nbsp;&nbsp;&nbsp;&nbsp;20.valueArray {\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;10.label.lang.dk = Bl&aring;\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;20.label.lang.dk = R&oslash;d\n&nbsp;&nbsp;&nbsp;&nbsp;}\n&nbsp;&nbsp;&nbsp;&nbsp;40.value.lang.dk = Send\n&nbsp;&nbsp;}\n&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:9:"radioWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:33:"Wraps the labels for radiobuttons";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:4:"type";s:8:"datatype";s:3:"int";s:11:"description";s:671:"Type (action=&quot;&quot; of the form):\n&nbsp;\n<b>Integer:</b> this is regarded to be a page in Typo3\n<b>String:</b> this is regarded to be a normal url (eg. &quot;formmail.php&quot; or &quot;fe_tce_db.php&quot;)\n<b>Empty;</b> the current page is chosen.\n<b>NOTE:</b> If type is integer/empty the form will be submitted to a page in Typo3 and if this page has a value for target/no_cache, then this will be used instead of the default target/no_cache below.\n&nbsp;\n<b>NOTE:</b> If the redirect-value is set, the redirect-target overriddens the target set by the action-url\n&nbsp;\n<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:6:"target";s:8:"datatype";s:6:"target";s:11:"description";s:27:"Default target of the form.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:6:"method";s:8:"datatype";s:11:"form-method";s:11:"description";s:20:"<b>Example: </b>\nGET";s:7:"default";s:4:"POST";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:8:"no_cache";s:8:"datatype";s:6:"string";s:11:"description";s:23:"Default no_cache-option";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:13:"noValueInsert";s:8:"datatype";s:7:"boolean";s:11:"description";s:428:"By default values that are submitted to the same page (and thereby same form, eg. at searchforms) are re-inserted in the form instead of any default-data that might be set up. \nThis, however, applies ONLY if the &quot;no_cache=1&quot; is set! (a page being cached may not include user-specific defaults in the fields of course...) \nIf you set this flag, &quot;noValueInsert&quot;, the content will always be the default content.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:20:"compensateFieldWidth";s:8:"datatype";s:6:"double";s:11:"description";s:85:"Overriding option to the config-value of the same name. See &quot;CONFIG&quot; above.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:12:"locationData";s:8:"datatype";s:16:"boolean / string";s:11:"description";s:1455:"If this value is true, then a hidden-field called &quot;locationData&quot; is added to the form. This field wil be loaded with a value like this:\n[page id]:[current record table]:[current record id]\nFor example, if a formfield is inserted on page with uid = &quot;100&quot;, as a page-content item from the table &quot;tt_content&quot; with id &quot;120&quot;, then the value would be &quot;100:tt_content:120&quot;.\nThe value is use by eg. the cObject SEARCHRESULT. If the value $GLOBALS[&quot;HTTP_POST_VARS&quot;][&quot;locationData&quot;] is detected here, the search is done as if it was performed on this page! This is very usefull if you want a search functionality implemented on a page with the &quot;stype&quot; field set to &quot;L1&quot; which means that the search is carried out from the first level in the rootline. \nSuppose you want the search to submit to a dedicated searchpage where ever. This page will then know - because of locationData - that the search was submittet from another place on the website.\nIf &quot;locationData&quot; is not only true but also set to &quot;HTTP_POST_VARS&quot; then the value will insert the content of $GLOBALS[&quot;HTTP_POST_VARS&quot;][&quot;locationData&quot;] instead of the true location data of the page. This should be done with search-fields as this will carry the initial searching start point with.\n<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:8:"redirect";s:8:"datatype";s:15:"string /stdWrap";s:11:"description";s:329:"url to redirect to (generates the hiddenfield &quot;redirect&quot;)\n&nbsp;\n<b>Integer:</b> this is regarded to be a page in Typo3\n<b>String:</b> this is regarded to be a normal url\n<b>Empty;</b> the current page is chosen.\n&nbsp;\n<b>NOTE:</b> If this value is set the target of this overriddes the target of the &quot;type&quot;.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:9:"recipient";s:8:"datatype";s:24:"(list of) string/stdWrap";s:11:"description";s:78:"email to formmail content to (generates the hiddenfield &quot;recipient&quot;)";s:7:"default";s:8:"No email";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:8:"goodMess";s:8:"datatype";s:6:"string";s:11:"description";s:172:"Message for the formevaluation function in case of correctly filled form.\n&nbsp;\n<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)";s:7:"default";s:10:"No message";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:7:"badMess";s:8:"datatype";s:6:"string";s:11:"description";s:223:"Prefixed Message for the formevaluation in case of missing required fields. \nThis message is shown above the list of fields.\n&nbsp;\n<b>NOTE:</b> May be overridden by the property override feature of the formdata (see above)";s:7:"default";s:10:"No message";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:5:"image";s:8:"datatype";s:20:"-&gt;IMAGE (cObject)";s:11:"description";s:159:"If this is a valid image the submitbutton is rendered as this image!!\n&nbsp;\n<b>NOTE:</b> CurrentValue is set to the caption-label before generating the image.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:15;a:6:{s:8:"property";s:6:"layout";s:8:"datatype";s:6:"string";s:11:"description";s:445:"This defines how the label and the field are placed towards each other.\n&nbsp;\n<b>Example:</b>\nThis substitutes the &quot;###FIELD###&quot; with the field data and the &quot;###LABEL###\' with labeldata. \n&nbsp;\n&lt;tr&gt;&lt;td&gt;###FIELD###&lt;/td&gt;&lt;td&gt; ###LABEL###&lt;/td&gt;&lt;/tr&gt;\n&nbsp;\nYou can also use the marker ###COMMENT### which is ALSO the label value inserted, but wrapped in .commentWrap stdWrap-properties (see below)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:16;a:6:{s:8:"property";s:9:"fieldWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:23:"Field: Wraps the fields";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:17;a:6:{s:8:"property";s:9:"labelWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:23:"Labels: Wraps the label";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:18;a:6:{s:8:"property";s:11:"commentWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:52:"Comments: Wrap for comments IF you use ###COMMENT###";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:19;a:6:{s:8:"property";s:3:"REQ";s:8:"datatype";s:7:"boolean";s:11:"description";s:58:"Defines if required-fields should be checked and marked up";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:20;a:6:{s:8:"property";s:13:"REQ.fieldWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:47:"Field: Wraps the fields, but for reuired fields";s:7:"default";s:34:"the &quot;fieldWrap&quot;-property";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:21;a:6:{s:8:"property";s:13:"REQ.labelWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:47:"Labels: Wraps the label, but for reuired fields";s:7:"default";s:34:"the &quot;labelWrap&quot;-property";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:22;a:6:{s:8:"property";s:10:"REQ.layout";s:8:"datatype";s:6:"string";s:11:"description";s:60:"The same as &quot;layout&quot; above, but for reuired fields";s:7:"default";s:31:"the &quot;layout&quot;-property";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:23;a:6:{s:8:"property";s:14:"COMMENT.layout";s:8:"datatype";s:6:"string";s:11:"description";s:32:"Alternative layout for comments.";s:7:"default";s:31:"the &quot;layout&quot;-property";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:24;a:6:{s:8:"property";s:12:"CHECK.layout";s:8:"datatype";s:6:"string";s:11:"description";s:33:"Alternative layout for checkboxes";s:7:"default";s:31:"the &quot;layout&quot;-property";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:25;a:6:{s:8:"property";s:12:"RADIO.layout";s:8:"datatype";s:6:"string";s:11:"description";s:35:"Alternative layout for radiobuttons";s:7:"default";s:31:"the &quot;layout&quot;-property";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:26;a:6:{s:8:"property";s:7:"stdWrap";s:8:"datatype";s:12:"-&gt;stdWrap";s:11:"description";s:46:"Wraps the hole form (before formtags is added)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:27;a:6:{s:8:"property";s:12:"hiddenFields";s:8:"datatype";s:18:"[array of cObject]";s:11:"description";s:195:"Used to set hiddenFields from TS.\n&nbsp;\n<b>Example:</b>\nhiddenFields.pid = TEXT\nhiddenFields.pid.value = 2\n&nbsp;\nThis makes a hidden-field with the name &quot;pid&quot; and value &quot;2&quot;.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:28;a:6:{s:8:"property";s:6:"params";s:8:"datatype";s:27:"form-element tag parameters";s:11:"description";s:258:"Extra parameters to form elements\n&nbsp;\n<b>Example:</b>\nparams = style=&quot;width:200px;&quot;\nparams.textarea = style=&quot;width:300px;&quot;\nparams.check =\n&nbsp;\nThis sets the default to 200 px width, but excludes check-boxes and sets textareas to 300.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('175', '270', 'a4573a8c9309f0e9d555a58941c6d79b', '', 'tsref:-&gt;makelinks', 'a:1:{s:4:"rows";a:8:{i:0;a:6:{s:8:"property";s:14:"http.extTarget";s:8:"datatype";s:6:"target";s:11:"description";s:22:"The target of the link";s:7:"default";s:4:"_top";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:9:"http.wrap";s:8:"datatype";s:4:"wrap";s:11:"description";s:20:"wrap around the link";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:19:"http.ATagBeforeWrap";s:8:"datatype";s:7:"boolean";s:11:"description";s:83:"If set, the link is first wrapped with <i>http.wrap</i> and then the &lt;A&gt;-tag.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:9:"http.keep";s:8:"datatype";s:66:"list: <i>&quot;scheme&quot;,&quot;path&quot;,&quot;query&quot;</i>";s:11:"description";s:472:"As default the link-text will be the full domain-name of the link.\n&nbsp;\n<b>Examples</b>:\nhttp://www.webaddress.rld/test/doc.php?id=3\n&quot;&quot;: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;www.webaddress.rld\n&quot;scheme&quot;: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;http://www.webaddress.rld\n&quot;scheme,path&quot;: &nbsp;&nbsp;&nbsp;&nbsp;http://www.webaddress.rld/test/doc.php\n&quot;scheme,path,query&quot;: http://www.webaddress.rld/test/doc.php?id=3";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:15:"http.ATagParams";s:8:"datatype";s:16:"&lt;A&gt;-params";s:11:"description";s:68:"Additional parameters\n&nbsp;\n<b>Example:</b>\nclass=&quot;board&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:11:"mailto.wrap";s:8:"datatype";s:4:"wrap";s:11:"description";s:20:"wrap around the link";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:21:"mailto.ATagBeforeWrap";s:8:"datatype";s:7:"boolean";s:11:"description";s:85:"If set, the link is first wrapped with mailto<i>.wrap</i> and then the &lt;A&gt;-tag.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:17:"mailto.ATagParams";s:8:"datatype";s:16:"&lt;A&gt;-params";s:11:"description";s:68:"Additional parameters\n&nbsp;\n<b>Example:</b>\nclass=&quot;board&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('176', '270', '48bb819f978041ca68258be0f64f7e38', '', 'tsref:-&gt;tags', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:8:"Array...";s:8:"datatype";s:55:"cObject <i>+stripNL</i>\n<i>+ breakoutTypoTagContent</i>";s:11:"description";s:1154:"Every entry in the <i>Array...</i> corresponds to a tag, that will be parsed. The elements MUST be in lowercase.\nEvery entry must be set to a content-object.\n&quot;current&quot; is set to the content of the tag, eg &lt;TAG&gt;content&lt;/TAG&gt;: here &quot;current&quot; is set to &quot;content&quot;.\n<b>Parameters:</b>\nParameters of the tag is set in $cObj-&gt;parameters (key is lowercased):\n&lt;TAG COLOR=&quot;red&quot;&gt;content&lt;/TAG&gt;\n=&gt; $cObj-&gt;parameters[color] = red\n<b>Special added properties to the content-object:</b>\n$cObj-&gt;parameters[allParams]: &nbsp;this is automatically set to the whole parameter-string of the tag, eg \' color=&quot;red&quot;\'\n[cObject].stripNL: is a boolean option, which tells <i>parseFunc</i> that NewLines before and after content of the tag should be stripped.\n[cObject].breakoutTypoTagContent: is a boolean option, which tells parseFunc that this block of content is breaking up the nonTypoTag content and that the content after this must be re-wrapped.\n&nbsp;\n<b>Examples:</b>\ntags.bold = TEXT\ntags.bold {\n&nbsp;&nbsp;current = 1\n&nbsp;&nbsp;wrap = &lt;B&gt; | &lt;/B&gt;\n}\ntags.bold.stripNL = 1";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('177', '270', '866514db4141aa8027197316d130fde6', '', 'page:-&gt;HTMLparser; tsref:-&gt;HTMLparser', 'a:1:{s:4:"rows";a:9:{i:0;a:6:{s:8:"property";s:9:"allowTags";s:8:"datatype";s:12:"list of tags";s:11:"description";s:20:"Default allowed tags";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:14:"tags.[tagname]";s:8:"datatype";s:28:"boolean/-&gt;HTMLparser_tags";s:11:"description";s:204:"Either set this property to 0 or 1 to allow or deny the tag. If you enter -&gt;HTMLparser_tags properties, those will automatically overrule this option, thus it\'s not needed then.\n[tagname] in lowercase.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:12:"localNesting";s:8:"datatype";s:42:"list of tags, must be among preserved tags";s:11:"description";s:100:"List of tags (among the already set tags), which will be forced to have the nesting-flag set to true";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:13:"globalNesting";s:8:"datatype";s:6:"(ibid)";s:11:"description";s:114:"List of tags (among the already set tags), which will be forced to have the nesting-flag set to &quot;global&quot;";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:15:"rmTagIfNoAttrib";s:8:"datatype";s:6:"(ibid)";s:11:"description";s:103:"List of tags (among the already set tags), which will be forced to have the rmTagIfNoAttrib set to true";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:8:"noAttrib";s:8:"datatype";s:6:"(ibid)";s:11:"description";s:154:"List of tags (among the already set tags), which will be forced to have the allowedAttribs value set to zero (which means, all attributes will be removed.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:10:"removeTags";s:8:"datatype";s:6:"(ibid)";s:11:"description";s:95:"List of tags (among the already set tags), which will be configured so they are surely removed.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:18:"keepNonMatchedTags";s:8:"datatype";s:29:"boolean / &quot;protect&quot;";s:11:"description";s:321:"If set (true=1), then all tags are kept regardless of tags present as keys in $tags-array.\nIf &quot;protect&quot;, then the preserved tags have their &lt;&gt; converted to &amp;lt; and &amp;gt;\nDefault is to REMOVE all tags, which are not specifically assigned to be allowed! So you might probably want to set this value!";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:16:"htmlSpecialChars";s:8:"datatype";s:10:"-1 / 0 / 1";s:11:"description";s:357:"This regards all content which is NOT tags:\n&quot;0&quot; means &quot;disabled&quot; - nothing is done\n&quot;1&quot; means the content outside tags is htmlspecialchar()\'ed (PHP-function which converts &amp;&quot;&lt;&gt; to &amp;...;)\n&quot;-1&quot; does the opposite of &quot;1&quot; - converts &amp;lt; to &lt;, &amp;gt; to &gt;, &amp;quot; to &quot; etc.";s:7:"default";s:0:"";s:12:"column_count";i:3;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('157', '270', '255d01ae1b0d5003500f17aeccc21a14', '', 'nbsp;', 'a:1:{s:4:"rows";a:34:{i:0;a:5:{s:8:"property";s:11:"&lt;tag&gt;";s:8:"datatype";s:43:"<b>&lt;BODY bgcolor=&quot;red&quot;&gt;</b>";s:11:"description";s:6:"&nbsp;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:1;a:5:{s:8:"property";s:5:"align";s:8:"datatype";s:12:"<b>right</b>";s:11:"description";s:70:"<b>right / left / center</b>\nDecides alignment, typically in HTML-tags";s:7:"default";s:4:"left";s:12:"column_count";i:4;}i:2;a:5:{s:8:"property";s:7:"VHalign";s:8:"datatype";s:70:"<i>Hori.align = right and Vert.align = center:</i>\n<b>r , c &nbsp;</b>";s:11:"description";s:94:"<b>r/c/l , t/c/b</b>\nHorizontal (right, center, left) , Vertical align (top / center / bottom)";s:7:"default";s:5:"l , t";s:12:"column_count";i:4;}i:3;a:5:{s:8:"property";s:8:"resource";s:8:"datatype";s:104:"<i>From the resourcefield:</i>\ntoplogo*.gif\n&nbsp;\n<i>Reference to filesystem:</i>\nfileadmin/picture.gif";s:11:"description";s:572:"1) A reference to a file from the resource-field in the template.\nYou can write the exact filename or you can include an asterisk (*) as wildcard.\nIt\'s recommended to include a &quot;*&quot; before the fileextension (see example to the left). This will ensure that the file is still referenced correct even if the template is copied and the file will have it\'s name prepended with numbers!!\n2) If the value contains a &quot;/&quot; it\'s expected to be a reference (absolute or relative) to a file on the file-system instead of the resource-field. No support for wildcards.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:4;a:5:{s:8:"property";s:11:"imgResource";s:8:"datatype";s:173:"Here &quot;file&quot; is a imgResource:\nfile = toplogo*.gif\nfile.width = 200\n&nbsp;\nGIFBUILDER:\nfile = GIFBUILDER\nfile {\n&nbsp;&nbsp;&nbsp;... (GIFBUILDER-properties here)\n}";s:11:"description";s:362:"1) A &quot;resource&quot; (see above) + imgResource-properties (see example to the left and object-reference below)\nFiletypes can be anything among the allowed types defined in the configuration variable $TYPO3_CONF_VARS[&quot;GFX&quot;][&quot;imagefile_ext&quot;] (localconf.php). Standard is pdf,gif,jpg,jpeg,tif,bmp,ai,pcx,tga,png.\n&nbsp;\n2) GIFBUILDER-object";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:5;a:5:{s:8:"property";s:9:"HTML-code";s:8:"datatype";s:43:"<b>&lt;B&gt;Some text in bold&lt;/B&gt;</b>";s:11:"description";s:14:"pure HTML-code";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:6;a:5:{s:8:"property";s:6:"target";s:8:"datatype";s:40:"<b>_top</b>\n<b>_blank</b>\n<b>content</b>";s:11:"description";s:117:"target in &lt;A&gt;-tag.\nThis is normally the same value as the name of the root-level object that defines the frame.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:7;a:5:{s:8:"property";s:14:"imageExtension";s:8:"datatype";s:67:"<b>jpg</b>\n<b>web&nbsp;&nbsp;&nbsp;&nbsp;<i>(gif or jpg ..)</i></b>";s:11:"description";s:379:"Image extensions can be anything among the allowed types defined in the global variable $TYPO3_CONF_VARS[&quot;GFX&quot;][&quot;imagefile_ext&quot;] (localconf.php). Standard is pdf,gif,jpg,jpeg,tif,bmp,ai,pcx,tga,png.\n<b>The value &quot;web&quot;</b> is special. This will just ensure that an image is converted to a web imageformat (gif or jpg) if it happens not to be already!";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:8;a:5:{s:8:"property";s:6:"degree";s:8:"datatype";s:13:"<b>&nbsp;</b>";s:11:"description";s:19:"-90 to 90, integers";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:9;a:5:{s:8:"property";s:13:"posint / int+";s:8:"datatype";s:13:"<b>&nbsp;</b>";s:11:"description";s:16:"Positive integer";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:10;a:5:{s:8:"property";s:3:"int";s:8:"datatype";s:13:"<b>&nbsp;</b>";s:11:"description";s:112:"integer\n(sometimes used generally though another type would have been more appropriate, like &quot;pixels&quot;)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:11;a:5:{s:8:"property";s:20:"str / string / value";s:8:"datatype";s:13:"<b>&nbsp;</b>";s:11:"description";s:111:"string.\n(sometimes used generally though another type would have been more appropriate, like &quot;align&quot;)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:12;a:5:{s:8:"property";s:7:"boolean";s:8:"datatype";s:8:"<b>1</b>";s:11:"description";s:61:"boolean\nnon-empty strings (but not zero) are &quot;true&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:13;a:5:{s:8:"property";s:8:"rotation";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:29:"integer, degrees from 0 - 360";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:14;a:5:{s:8:"property";s:7:"x,y,w,h";s:8:"datatype";s:16:"<b>10,10,5,5</b>";s:11:"description";s:73:"x,y is the offset from the upper left corner.\nw,h is the width and height";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:15;a:5:{s:8:"property";s:10:"HTML-color";s:8:"datatype";s:26:"<b>red</b>\n<b>#ffeecc </b>";s:11:"description";s:581:"<b>HTML-color codes:</b>\n&nbsp;\nBlack = &quot;#000000&quot; &nbsp;&nbsp;\nSilver = &quot;#C0C0C0&quot; &nbsp;&nbsp;\nGray = &quot;#808080&quot; &nbsp;&nbsp;\nWhite = &quot;#FFFFFF&quot; &nbsp;&nbsp;\nMaroon = &quot;#800000&quot; &nbsp;&nbsp;\nRed = &quot;#FF0000&quot; &nbsp;&nbsp;\nPurple = &quot;#800080&quot; &nbsp;&nbsp;\nFuchsia = &quot;#FF00FF&quot; &nbsp;&nbsp;\nGreen = &quot;#008000&quot;\nLime = &quot;#00FF00&quot;\nOlive = &quot;#808000&quot;\nYellow = &quot;#FFFF00&quot;\nNavy = &quot;#000080&quot;\nBlue = &quot;#0000FF&quot;\nTeal = &quot;#008080&quot;\nAqua = &quot;#00FFFF&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:16;a:5:{s:8:"property";s:12:"GraphicColor";s:8:"datatype";s:440:"<b>red<i>&nbsp;&nbsp;&nbsp;&nbsp;(HTML-color)</i></b>\n<b>#ffeecc <i>&nbsp;&nbsp;&nbsp;&nbsp;(HTML-color)</i></b>\n<b>255,0,255<i>&nbsp;&nbsp;&nbsp;&nbsp;(RGB-integers)</i></b>\n<i>&nbsp;</i>\n<i>Extra:</i>\n<b>red<i> : *0.8</i><i>&nbsp;&nbsp;&nbsp;&nbsp;(&quot;red&quot; is darkend by factor 0.8)</i></b>\n<b>#ffeecc <i>: +16</i><i>&nbsp;&nbsp;&nbsp;&nbsp;(&quot;ffeecc&quot; is going to #fffedc because 16 is added)</i></b>\n<b><i>&nbsp;</i></b>";s:11:"description";s:393:"The color can be given as HTML-colors or as a comma-seperated list of RGB-values (integers)\nYou can add an extra parameter that will modify the color mathematically:\nSyntax: &nbsp;\n[colordef] : [modifier]\nwhere modifier can be and integer which is added/subtracted to the three RGB-channels or a floatingpoint with an &quot;*&quot; before, which will then multiply the values with that factor.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:17;a:5:{s:8:"property";s:7:"page_id";s:8:"datatype";s:21:"<b>this</b>\n<b>34</b>";s:11:"description";s:54:"A page id (int) or &quot;this&quot; (=current page id)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:18;a:5:{s:8:"property";s:6:"pixels";s:8:"datatype";s:10:"<b>345</b>";s:11:"description";s:14:"pixel-distance";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:19;a:5:{s:8:"property";s:4:"list";s:8:"datatype";s:23:"<b>item,item2,item3</b>";s:11:"description";s:14:"list of values";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:20;a:5:{s:8:"property";s:7:"margins";s:8:"datatype";s:105:"<i>This sets leftmargin to 10 and bottom-margin to 5. Top and right is not set (zero)</i>\n<b>10,0,0,5</b>";s:11:"description";s:32:"l,t,r,b\nleft, top, right, bottom";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:21;a:5:{s:8:"property";s:4:"wrap";s:8:"datatype";s:142:"<i>This will cause the value to be wrapped in a font-tag coloring the value red:</i>\n<b>&lt;font color=&quot;red&quot;&gt; | &lt;/font&gt;</b>";s:11:"description";s:153:"&lt;...&gt; | &lt;/...&gt;\nUsed to wrap something. The part on the left and right of the vertical line is placed on the left and right side of the value.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:22;a:5:{s:8:"property";s:8:"linkWrap";s:8:"datatype";s:115:"<i>This will make a link to the root-level of a website:</i>\n<b>&lt;A HREF=&quot;?id={0}&quot;&gt; | &lt;/A&gt;</b>";s:11:"description";s:342:"&lt;.. {x}.&gt; | &lt;/...&gt;\n{x}; x is an integer (0-9) and points to a key in the PHP-array rootLine. The key is equal to the level the current page is on measured relatively to the root of the website.\nIf the key exists the uid of the level that key pointed to is inserted instead of {x}.\nThus we can insert page_ids from previous levels.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:23;a:5:{s:8:"property";s:4:"case";s:8:"datatype";s:12:"<b>upper</b>";s:11:"description";s:53:"&quot;upper&quot; / &quot;lower&quot;\nCase-conversion";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:24;a:5:{s:8:"property";s:5:"space";s:8:"datatype";s:12:"<b>5 | 5</b>";s:11:"description";s:86:"&quot;before | after&quot;\nUsed for content and sets space &quot;before | after&quot;.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:25;a:5:{s:8:"property";s:9:"date-conf";s:8:"datatype";s:60:"<b>d-m-y&nbsp;&nbsp;&nbsp;&nbsp;<i>(dd-mm-yy format)</i></b>";s:11:"description";s:861:"See PHP function Date()!\n&nbsp;\na - &quot;am&quot; or &quot;pm&quot;\nA - &quot;AM&quot; or &quot;PM&quot;\nd - day of the month, numeric, 2 digits (with leading zeros)\nD - day of the week, textual, 3 letters; i.e. &quot;Fri&quot;\nF - month, textual, long; i.e. &quot;January&quot;\nh - hour, numeric, 12 hour format\nH - hour, numeric, 24 hour format\ni - minutes, numeric\nj - day of the month, numeric, without leading zeros\nl (lowercase \'L\') - day of the week, textual, long; i.e. &quot;Friday&quot;\nm - month, numeric\nM - month, textual, 3 letters; i.e. &quot;Jan&quot;\ns - seconds, numeric\nS - English ordinal suffix, textual, 2 characters; i.e. &quot;th&quot;, &quot;nd&quot;\nU - seconds since the epoch\nY - year, numeric, 4 digits\nw - day of the week, numeric, 0 represents Sunday\ny - year, numeric, 2 digits\nz - day of the year, numeric; i.e. &quot;299&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:26;a:5:{s:8:"property";s:13:"strftime-conf";s:8:"datatype";s:113:"Date &quot;DD-MM-YY&quot; = \n<b>%e:%m:%y</b>\nTime &quot;HH:MM:SS&quot; = \n<b>%H:%M:%S</b>\nor &nbsp;just\n<b>%T</b>";s:11:"description";s:2115:"%a - abbreviated weekday name according to the current locale\n%A - full weekday name according to the current locale\n%b - abbreviated month name according to the current locale\n%B - full month name according to the current locale\n%c - preferred date and time representation for the current locale\n%C - century number (the year divided by 100 and truncated to an integer, range 00 to 99)\n%d - day of the month as a decimal number (range 00 to 31)\n%D - same as %m/%d/%y\n<b>%e - day of the month as a decimal number, a single digit is preceded by a space (range \' 1\' to \'31\') </b>\n%h - same as %b\n<b>%H - hour as a decimal number using a 24-hour clock (range 00 to 23) </b>\n%I - hour as a decimal number using a 12-hour clock (range 01 to 12)\n%j - day of the year as a decimal number (range 001 to 366)\n<b>%m - month as a decimal number (range 01 to 12) </b>\n<b>%M - minute as a decimal number </b>\n%n - newline character\n%p - either `am\' or `pm\' according to the given time value, or the corresponding strings for the current locale\n%r - time in a.m. and p.m. notation\n%R - time in 24 hour notation\n<b>%S - second as a decimal number </b>\n%t - tab character\n%T - current time, equal to %H:%M:%S\n%u - weekday as a decimal number [1,7], with 1 representing Monday\n%U - week number of the current year as a decimal number, starting with the first Sunday as the first day of the first week\n%V - The ISO 8601:1988 week number of the current year as a decimal number, range 01 to 53, where week 1 is the first week that has at least 4 days in the current year, and with Monday as the first day of the week.\n%W - week number of the current year as a decimal number, starting with the first Monday as the first day of the first week\n%w - day of the week as a decimal, Sunday being 0\n%x - preferred date representation for the current locale without the time\n%X - preferred time representation for the current locale without the date\n<b>%y - year as a decimal number without a century (range 00 to 99) </b>\n%Y - year as a decimal number including the century\n%Z - time zone or name or abbreviation\n%% - a literal `%\' character";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:27;a:5:{s:8:"property";s:9:"UNIX-time";s:8:"datatype";s:52:"<i>Seconds to 07/04 2000 23:58:</i>\n<b>955144722</b>";s:11:"description";s:25:"Seconds since 1/1 1970...";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:28;a:5:{s:8:"property";s:4:"path";s:8:"datatype";s:23:"<i>fileadmin/stuff/</i>";s:11:"description";s:53:"path relative to the directory from which we operate.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:29;a:5:{s:8:"property";s:16:"&lt;tag&gt;-data";s:8:"datatype";s:13:"<i>&nbsp;</i>";s:11:"description";s:73:"<b>Example:</b>\n<i>&lt;frameset&gt;-data: row</i>\ncould be \'<i>150,*</i>\'";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:30;a:5:{s:8:"property";s:18:"&lt;tag&gt;-params";s:8:"datatype";s:13:"<i>&nbsp;</i>";s:11:"description";s:112:"<b>Example:</b>\n<i>&lt;frameset&gt;-params</i>\ncould be \'<i>border=&quot;0&quot; framespacing=&quot;0&quot;</i>\'";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:31;a:5:{s:8:"property";s:7:"getText";s:8:"datatype";s:1830:"<i>get content from the $cObj-&gt;data-array[<b>header</b>]:</i>\n<b>= field : header</b>\n<i>get content from the $cObj-&gt;parameters-array[<b>color</b>]:</i>\n<b>= parameter : color</b>\n<i>get content from the $GLOBALS[&quot;TSFE&quot;]-&gt;register[<b>color</b>]:</i>\n<b>= register : color</b>\n<i>get the title of the page on the first level of the rootline:</i>\n<b>= leveltitle : 1</b>\n<i>get the title of the page on the level right below the current page AND if that is not present, walt to the bottom of the rootline until there\'s a title:</i>\n<b>= leveltitle : -2 , slide</b>\n<i>get the id of the root-page of the website (level zero)</i>\n<b>= leveluid : 0</b>\n<i>get the env var HTTP_REFERER:</i>\n<b>= getenv : HTTP_REFERER</b>\n<i>get the env variable $HTTP_COOKIE_VARS[some_cookie]:</i>\n<b>= global : HTTP_COOKIE_VARS | some_cookie</b>\n<i>get the current time formatted dd-mm-yy:</i>\n<b>= date : d-m-y</b>\n<i>get the current page-title:</i>\n<b>= page : title</b>\n<i>get the current value:</i>\n<b>= current : 1</b>\nget input value from query string, (&amp;stuff=)\n<b>= GPvar : stuff</b>\nget the current id\n<b>= TSFE : id</b>\nget the value of the header of record with uid 234 from table tt_content:\n<b>= DB : tt_content:234:header</b>\n<b>= fullRootLine : -1, title</b>\nThis gets the title &quot;1. page before&quot; in a page tree like the one below provided we are are the page &quot;Here you are!&quot; (or &quot;Site root&quot;) and this TypoScript is in the template with root at &quot;Site root&quot;. Red numbers indicate what values of <i>keynumber</i> would point to:\n- Page tree root &nbsp;&nbsp;-2\n&nbsp;&nbsp;&nbsp;|- 1. page before &nbsp;&nbsp;-1 \n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;|- Site root (root template here!) &nbsp;&nbsp;0\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;|- Here you are! &nbsp;1";s:11:"description";s:2493:"This returns a value from somewhere in PHP-array, defined by the type. The syntax is &quot;type : pointer&quot;\n&nbsp;\n<b>field :</b> [fieldname from the current <i>$cObj</i>-&gt;data-array in the cObj.]\nAs default the <i>$cObj</i>-&gt;data-array is $GLOBALS[&quot;TSFE&quot;]-&gt;page (record of the current page!)\nIn TMENU: <i>$cObj</i>-&gt;data is set to the page-record for each menuitem.\nIn CONTENT/RECORDS <i>$cObj</i>-&gt;data is set to the actual record\nIn GIFBUILDER <i>$cObj</i>-&gt;data is set to the data GIFBUILDER is supplied with.\n<b>parameter :</b> [fieldname from the current <i>$cObj</i>-&gt;parameters-array in the cObj.]\nSee &nbsp;-&gt;parseFunc!\n<b>register :</b> [fieldname from the $GLOBALS[&quot;TSFE&quot;]-&gt;register]\nSee cObject &quot;LOAD_REGISTER&quot;\n<b>leveltitle, leveluid, levelmedia:</b> [levelTitle, uid or media in rootLine, 0- , negative = from behind, &quot; , slide&quot; parameter forces a walk to the bottom of the rootline until there\'s a &quot;true&quot; value to return. Useful with levelmedia.]\n<b>global :</b> [GLOBAL-var, split with | if you want to get from an array! DEPRECIATED, use GPvar, TSFE or getenv]\n<b>date :</b> [date-conf]\n<b>page :</b> [current page record]\n<b>current : 1</b> (gets \'current\' value)\n<b>level : 1 &nbsp;(gets the rootline level of the current page)</b>\n<b>GPvar:</b> Value from GET or POST method. Use this instead of global\n<b>TSFE: </b>Value from TSFE global main object\n<b>getenv:</b> Value from environment vars\n<b>DB:</b> Value from database, syntax is [tablename] : [uid] : [field]. Any record from a table in TCA can be selected here. Only marked-deleted records does not return a value here.\n<b>--------------------</b>\n<b>Getting more values.</b>\nBy separating the value of getText with &quot;//&quot; (double slash) you let getText fetch the first value. If it appears empty (&quot;&quot; or zero) the next value is fetched and so on. Example:\n= field:header // field:title // field:uid\nThis gets &quot;title&quot; if &quot;header&quot; is empty. If &quot;title&quot; is also empty it gets field &quot;uid&quot;\n<b>fullRootLine </b>:\nGetting a value from the rootline (TSFE-&gt;rootLine) going all the way to the root of the page tree.\n<i>[keynumber, fieldname, &quot;slide&quot;]</i>\nKeynumber is relative to the current site root, so negative numbers refer to subsequent levels below the site root. Fieldname is the name of the datafield to get. For &quot;slide&quot;, see <i>levelmedia</i> type above.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:32;a:5:{s:8:"property";s:3:"dir";s:8:"datatype";s:236:"<i>returns a list of all pdf, gif and jpf-filer from fileadmin/files/ sorted by their name</i> reversely and with the full path (with &quot;<i>fileadmin/files/&quot; prepended)</i>\n<b>fileadmin/files/ | pdf,gif,jpg | name</b> | r | true";s:11:"description";s:383:"[path relative to the webroot of the site] | [list of valid extensions] | [sorting: name, size, ext, date] | &nbsp;[reverse: &quot;r&quot;] &nbsp;| &nbsp;[return full path: boolean\nFiles matching is returned in a comma-separated string.\n<b>Note:</b>\nThe value of config-option &quot;lockFilePath&quot; must equal the first part of the path. Thereby the path is locked to that folder.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;}i:33;a:5:{s:8:"property";s:13:"function-name";s:8:"datatype";s:98:"Function:\n<b>user_reverseString</b>\nMethod in class:\n<b>user_stringReversing-&gt;reverseString</b>";s:11:"description";s:741:"Indicates a function or method in a class to call. See more information at the USER cObject.\nDepending on implementation the class or function name (but not the method name) should probably be prefixed with &quot;user_&quot;. This can be changed in the TYPO3_CONF_VARS config though. Also the function / method is normally called with 2 parameters, typ. $conf (TS config) and $content (some content to be processed and returned)\nAlso if you call a method in a class, it is checked (when using the USER/USER_INT objects) whether a class with the same name, but prefixed with &quot;ux_&quot; is present and if so, this class is instantiated instead. See &quot;Inside Typo3&quot; document for more information on extending the classes in Typo3!";s:7:"default";s:13:"<b>&nbsp;</b>";s:12:"column_count";i:4;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('105', '263', '284eaff2c8b565d4257002d25e85e0ec', '', 'beuser', 'a:1:{s:4:"rows";a:6:{i:0;a:6:{s:8:"property";s:8:"admPanel";s:8:"datatype";s:13:"-&gt;ADMPANEL";s:11:"description";s:43:"Options regarding the front-end admin panel";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:7:"options";s:8:"datatype";s:12:"-&gt;OPTIONS";s:11:"description";s:29:"Options for the user, various";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:3:"mod";s:8:"datatype";s:38:"<i>(see -&gt;MOD of Page TSconfig)</i>";s:11:"description";s:41:"Overriding values for the backend modules";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:29:"setup.defaults\nsetup.override";s:8:"datatype";s:10:"-&gt;SETUP";s:11:"description";s:85:"Default values and override values for the user settings known from the setup module.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:11:"TCAdefaults";s:8:"datatype";s:27:"[tablename].[field] = value";s:11:"description";s:720:"Sets default values for records. The order of default values when creating new records in the backend is this:\nValue from tables.php\nValue from userTS\nValue from submitted global array, defVals (to load_edit.php / transferdata.php)\nValue from previous record based on \'useColumnsForDefaultValues\'\n(Loaded with transferdata.php to backend)\n&nbsp;\nHowever the order for default values used by tcemain.php if a certain field is not granted access to for user will be:\nValue from tables.php\nValue from userTS\n&nbsp;\nSo these values will be authoritative if the user has no access to the field anyway.\n&nbsp;\n<b>Example:</b>\nThis sets the default hidden flag for pages to &quot;clear&quot;\n&nbsp;\nTCAdefaults.pages.hidden = 0";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:4:"user";s:8:"datatype";s:3:"???";s:11:"description";s:65:"This is for custom purposed. You can use if for your own games...";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('106', '263', '7cd94e953cc449fe4c0856671acf229d', '', 'beuser:admPanel', 'a:1:{s:4:"rows";a:3:{i:0;a:6:{s:8:"property";s:6:"enable";s:8:"datatype";s:8:"[object]";s:11:"description";s:217:"Used to enable the various parts of the panel for users.\nAll values are 0/1 booleans.\n<b>&nbsp;</b>\n<b>General:</b>\n.all: enables all modules\n<b>&nbsp;</b>\n<b>Modules:</b>\n.preview\n.cache\n.publish\n.edit\n.tsdebug\n.info";s:7:"default";s:57:"(For admin-users, all = 1 is default! Hardcoded in class)";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:4:"hide";s:8:"datatype";s:7:"boolean";s:11:"description";s:92:"If set, the panel will not display in the bottom of the page. This has only a visual effect.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:35:"module.edit&nbsp;&nbsp;&nbsp;&nbsp;";s:8:"datatype";s:8:"[object]";s:11:"description";s:547:"<b>.forceDisplayIcons</b> (boolean):\n<i>Forces edit-panels to appear regardless of the selectorbox.</i>\n<b>.forceDisplayFieldIcons</b> (boolean):\n<i>Forces edit-icons to appear regardless of the selectorbox.</i>\n<b>.forceNoPopup</b> (boolean):\n<i>Forces edit-forms to open in same window - not pop up window.</i>\n<i>&nbsp;</i>\n<i>Example, that forces the display of the edit icons without displaying the admin-panel itself:</i>\n&nbsp;\nadmPanel {\n&nbsp;&nbsp;enable.edit = 1\n&nbsp;&nbsp;module.edit.forceDisplayFieldIcons = 1\n&nbsp;&nbsp;hide = 1\n}";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('107', '263', '114e940842aa2d2d8a1efb37b1191103', '', 'beuser:options', 'a:1:{s:4:"rows";a:27:{i:0;a:6:{s:8:"property";s:20:"dontMountAdminMounts";s:8:"datatype";s:7:"boolean";s:11:"description";s:132:"<b>NOTE:</b> Only for admin-users. For other users it has no effect.\nThis options prevents the root to be mounted for an admin user.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:10:"RTEkeyList";s:8:"datatype";s:18:"[list of elements]";s:11:"description";s:517:"This is a list of the Rich Text Editor buttons the user may have displayed. The user will not sett any buttons not listed here.\nEither enter a list of these elements:\n&nbsp;\ncut,copy,paste,formatblock,fontstyle,fontsize,bold,italic, underline,left,center,right,orderedlist,unorderedlist,outdent, indent,line,link,table,image,textcolor\n&nbsp;\nor specify all with a wildcard &quot;*&quot; for everything\nchMode is also an element you can use and that will display an option to look at the source-code instead of WYSIWYG";s:7:"default";s:45:"*\n(If value is not set at all, *, is default)";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:16:"clearCache.pages";s:8:"datatype";s:7:"boolean";s:11:"description";s:53:"This will allow a user to clear the whole page cache.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:14:"clearCache.all";s:8:"datatype";s:7:"boolean";s:11:"description";s:82:"This will allow a user to clear all cache (that is everything including templates)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:8:"lockToIP";s:8:"datatype";s:6:"string";s:11:"description";s:529:"List of IP-numbers with wildcards.\n&nbsp;\n<b>Note:</b> This option is enabled only if the TYPO3_CONF_VARS[BE][enabledBeUserIPLock] configuration is true.\n<b>&nbsp;</b>\n<b>Examples:</b>\n192.168.*.* &nbsp;&nbsp;&nbsp;\n- will allow all from 192.168-network\n&nbsp;\n192.168.*.*, 212.22.33.44 &nbsp;&nbsp;&nbsp;\n- will allow all from 192.168-network plus all from REMOTE_ADDR &nbsp;212.22.33.44\n&nbsp;\n192.168, 212.22.33.44 &nbsp;&nbsp;&nbsp;\n- the same as the previous. Leaving out parts of the IP address is the same as wild cards...";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:13:"saveClipboard";s:8:"datatype";s:7:"boolean";s:11:"description";s:129:"If set, the clipboard content will be preserved for the next login. Normally the clipboard content lasts only during the session.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:19:"clipboardNumberPads";s:8:"datatype";s:10:"int (0-20)";s:11:"description";s:65:"This allows you to enter how many pads you want on the clipboard.";s:7:"default";s:1:"3";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:21:"enableABClipboardInCB";s:8:"datatype";s:7:"boolean";s:11:"description";s:84:"If set, the Alternative Backend clipboard is enabled in the classic backend as well.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:20:"disableDocModuleInAB";s:8:"datatype";s:7:"boolean";s:11:"description";s:77:"If set, the doc module is not displayed in the AB even if enabled in general.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:13:"shortcutFrame";s:8:"datatype";s:7:"boolean";s:11:"description";s:63:"If set, the shortcut frame in the bottom of the window appears.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:14:"shortcutGroups";s:8:"datatype";s:17:"Array of integers";s:11:"description";s:217:"Setting which shared shortCutGroups are loaded for the user.\n&nbsp;\n<b>Example:</b>\nshortcutGroups {\n&nbsp;&nbsp;1=1\n&nbsp;&nbsp;2=1\n&nbsp;&nbsp;3=\n}\n&nbsp;\nShared shortcut group 1 and 2 is loaded, but 3 is not loaded";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:33:"shortcut_onEditId_dontSetPageTree";s:8:"datatype";s:7:"boolean";s:11:"description";s:120:"If set, the page tree is NOT opened to the page being edited when an id number is entered in the &quot;Edit Id&quot; box";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:38:"shortcut_onEditId_keepExistingExpanded";s:8:"datatype";s:7:"boolean";s:11:"description";s:182:"If set, the existing expanded pages in the page tree are not collapsed when an id is entered in the &quot;Edit Id&quot; box.\n(provided .shortcut_onEditId_dontSetPageTree is not set!)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:25:"mayNotCreateEditShortcuts";s:8:"datatype";s:7:"boolean";s:11:"description";s:91:"If set, then the user cannot create or edit shortcuts. Depends on .shortcutFrame being set.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:17:"createFoldersInEB";s:8:"datatype";s:7:"boolean";s:11:"description";s:103:"If set, a createFolders option appears in the element browser (for admin-users this is always enabled).";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:15;a:6:{s:8:"property";s:12:"noThumbsInEB";s:8:"datatype";s:7:"boolean";s:11:"description";s:67:"If set, then image thumbnails are not shown in the element browser.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:16;a:6:{s:8:"property";s:24:"noThumbsInRTEimageSelect";s:8:"datatype";s:7:"boolean";s:11:"description";s:67:"As &nbsp;.noThumbsInEB but for the Rich Text Editor image selector.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:17;a:6:{s:8:"property";s:21:"uploadFieldsInTopOfEB";s:8:"datatype";s:7:"boolean";s:11:"description";s:82:"If set, the upload-fields in the element browser are put in the top of the window.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:18;a:6:{s:8:"property";s:13:"disableJSStop";s:8:"datatype";s:7:"boolean";s:11:"description";s:52:"If set, detection of JSStop is switched of this user";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:19;a:6:{s:8:"property";s:29:"saveDocNew\nsaveDocNew.[table]";s:8:"datatype";s:25:"boolean / &quot;top&quot;";s:11:"description";s:457:"If set, a button &quot;Save and create new&quot; will appear in TCEFORMs.\nAny value set for a single table will override the default value set to the object &quot;saveDocNew&quot;.\n&nbsp;\n<b>Example:</b>\nIn this example the button is disabled for all tables, except tt_content where it will appear, and in addition create the records in the top of the page (default is after instead of top).\n&nbsp;\noptions.saveDocNew = 0\noptions.saveDocNew.tt_content = top";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:20;a:6:{s:8:"property";s:35:"disableDelete\ndisableDelete.[table]";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:121:"Disables the &quot;Delete&quot; button in TCEFORMs.\nOverriding for single tables works like &quot;saveDocNew&quot; above.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:21;a:6:{s:8:"property";s:31:"showHistory\nshowHistory.[table]";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:123:"Shows link to the history for the record in TCEFORMs.\nOverriding for single tables works like &quot;saveDocNew&quot; above.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:22;a:6:{s:8:"property";s:77:"pageTree.disableIconLinkToContextmenu\nfolderTree.disableIconLinkToContextmenu";s:8:"datatype";s:31:"boolean / &quot;titlelink&quot;";s:11:"description";s:209:"If set, the page/folder-icons in the page/folder tree will not activate the clickmenu.\nIf the value is set &quot;titlelink&quot; then the icon will instead be wrapped with the same link as the title.\n(AB only)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:23;a:6:{s:8:"property";s:30:"contextMenu.[key].disableItems";s:8:"datatype";s:13:"list of items";s:11:"description";s:853:"List of context menu (&quot;clickmenu&quot;) items to disable.\n&quot;key&quot; points to which kind of icon that brings up the menu, and possible options are &quot;pageTree&quot;, &quot;pageList&quot;, &quot;folderTree&quot;, &quot;folderList&quot;. &quot;page&quot; and &quot;folder&quot; obviously points to either the Web og File main module. &quot;Tree&quot; and &quot;List&quot; points to whether the menu is activated from the page/folder tree or the listing of records/files\nItems to disable are (for &quot;page&quot; type - that is database records):\n&nbsp;\nview,edit,hide,new,info,copy,cut,paste,delete,move_wizard,\nhistory,perms,new_wizard,hide,edit_access,edit_pageheader,db_list\n&nbsp;\nItems to disable are (for &quot;folder&quot; type - that is files/folders):\nedit,upload,rename,new,info,copy,cut,paste,delete\n(AB only, the small topframe)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:24;a:6:{s:8:"property";s:29:"contextMenu.options.leftIcons";s:8:"datatype";s:7:"boolean";s:11:"description";s:71:"If set, the icons in the clickmenu appears to the left instead of right";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:25;a:6:{s:8:"property";s:36:"contextMenu.options.clickMenuTimeOut";s:8:"datatype";s:10:"int, 1-100";s:11:"description";s:93:"Number of seconds the click menu is visible in the top frame before it disappears by it self.";s:7:"default";s:1:"5";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:26;a:6:{s:8:"property";s:49:"contextMenu.options.alwaysShowClickMenuInTopFrame";s:8:"datatype";s:7:"boolean";s:11:"description";s:148:"If set, then the clickmenu in the top frame is always shown. Default is that it\'s shown only if the pop-up menus are disabled by user or by browser.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('108', '263', '64dfc4b3acce1eaea54363c984c36980', '', 'beuser:setup.default/setup.override', 'a:1:{s:4:"rows";a:33:{i:0;a:6:{s:8:"property";s:15:"pane_clip_count";s:8:"datatype";s:4:"int+";s:11:"description";s:31:"Number of tabs on the clipboard";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:8:"pane_web";s:8:"datatype";s:21:"<i>list of string</i>";s:11:"description";s:44:"Page Tree Pane Labels (Classic Backend only)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:9:"pane_file";s:8:"datatype";s:21:"<i>list of string</i>";s:11:"description";s:49:"Directory Tree Pane Labels (Classic Backend only)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:19:"thumbnailsByDefault";s:8:"datatype";s:7:"boolean";s:11:"description";s:26:"Show Thumbnails by default";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:14:"emailMeAtLogin";s:8:"datatype";s:7:"boolean";s:11:"description";s:57:"Notify me by email, when somebody logs in from my account";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:12:"openDirectly";s:8:"datatype";s:7:"boolean";s:11:"description";s:29:"Open Typo3 Interface at Login";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:18:"alternativeBackend";s:8:"datatype";s:7:"boolean";s:11:"description";s:76:"If set, the Alternative Backend will load instead of the classic by default.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:17:"startInTaskCenter";s:8:"datatype";s:7:"boolean";s:11:"description";s:102:"If set, then the backend will start up in the task center (task center should be enabled for the user)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:14:"localFrameEdit";s:8:"datatype";s:7:"boolean";s:11:"description";s:136:"If set, all edit-icons (except from clickMenus) will open the record in the local frame instead of loading a document in the doc-module.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:9;a:6:{s:8:"property";s:14:"JSwindowParams";s:8:"datatype";s:6:"string";s:11:"description";s:268:"This string will override the system parameters opening the classic interface in a new window. By setting values here, you can fully customize the way Typo3 loads in the window.\n&nbsp;\n<b>Example:</b>\nstatus=1,menubar=0,resizable=1, screenX=1, screenY=1, left=1, top=1";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:10;a:6:{s:8:"property";s:17:"saveTreePositions";s:8:"datatype";s:7:"boolean";s:11:"description";s:27:"Remember position at Logout";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:11;a:6:{s:8:"property";s:8:"helpText";s:8:"datatype";s:7:"boolean";s:11:"description";s:30:"Show help text when applicable";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:12;a:6:{s:8:"property";s:8:"titleLen";s:8:"datatype";s:4:"int+";s:11:"description";s:17:"Max. Title Length";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:13;a:6:{s:8:"property";s:17:"edit_wideDocument";s:8:"datatype";s:7:"boolean";s:11:"description";s:24:"Wide document background";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:14;a:6:{s:8:"property";s:8:"edit_RTE";s:8:"datatype";s:7:"boolean";s:11:"description";s:23:"Enable Rich Text Editor";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:15;a:6:{s:8:"property";s:15:"edit_maxRecords";s:8:"datatype";s:4:"int+";s:11:"description";s:47:"Max number of content records to edit at a time";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:16;a:6:{s:8:"property";s:20:"edit_docModuleUpload";s:8:"datatype";s:7:"boolean";s:11:"description";s:35:"File upload directly in Doc. module";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:17;a:6:{s:8:"property";s:13:"navFrameWidth";s:8:"datatype";s:4:"int+";s:11:"description";s:99:"The width in pixels of the navigation frame in the Page and File main modules (Alternative Backend)";s:7:"default";s:10:"245 pixels";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:18;a:6:{s:8:"property";s:4:"winH";s:8:"datatype";s:4:"int+";s:11:"description";s:42:"Typo3 Window, width (Classic Backend only)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:19;a:6:{s:8:"property";s:4:"winW";s:8:"datatype";s:4:"int+";s:11:"description";s:43:"Typo3 Window, height (Classic Backend only)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:20;a:6:{s:8:"property";s:4:"lang";s:8:"datatype";s:12:"language-key";s:11:"description";s:131:"One of the language-keys. See t3lib/config_default.php for current options. Eg. &quot;dk&quot;, &quot;de&quot;, &quot;es&quot; etc.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:21;a:6:{s:8:"property";s:10:"copyLevels";s:8:"datatype";s:4:"int+";s:11:"description";s:84:"Recursive Copy: Enter the number of page sublevels to include, when a page is copied";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:22;a:6:{s:8:"property";s:15:"recursiveDelete";s:8:"datatype";s:7:"boolean";s:11:"description";s:74:"Recursive Delete(!): Allow ALL subpages to be deleted when deleting a page";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:23;a:6:{s:8:"property";s:20:"deleteCmdInClipboard";s:8:"datatype";s:7:"boolean";s:11:"description";s:44:"Allow \'Delete\' command in the clipboard menu";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:24;a:6:{s:8:"property";s:16:"allSaveFunctions";s:8:"datatype";s:7:"boolean";s:11:"description";s:45:"Display all save functions in Doc-module menu";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:25;a:6:{s:8:"property";s:15:"neverHideAtCopy";s:8:"datatype";s:7:"boolean";s:11:"description";s:72:"If set, then the hideAtCopy feature for records in TCE will not be used.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:26;a:6:{s:8:"property";s:13:"condensedMode";s:8:"datatype";s:7:"boolean";s:11:"description";s:225:"If set, the Alternative Backend will not load the Web-submodules and File-submodules in a frameset by allow the page and folder trees to load the submodule in it\'s own frame. This allows for a better display on small screens.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:27;a:6:{s:8:"property";s:10:"noMenuMode";s:8:"datatype";s:16:"boolean / string";s:11:"description";s:583:"If set, the Alternative Backend will not load the left menu frame but rather put a selector-box menu in the topframe. This saves a lot of space on small screens. Also icons will not be displayed in the clickmenu panel in the top.\n&nbsp;\n<b>Value &quot;icons&quot;: </b>\nSetting noMenuMode to &quot;icons&quot; will still remove the menu, but instead of the selectorbox menu you will have the whole clickmenu panel as a menu with the icons only as the hidden state of the clickmenu panel. This is extremely nice (in my opinion) for experienced users who know the icons of the modules.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:28;a:6:{s:8:"property";s:20:"dontEditInPageModule";s:8:"datatype";s:7:"boolean";s:11:"description";s:220:"From version 3.3 pages are edited in the Web&gt;Page module and by default the clickmenu in the page tree will open the page there if the Web&gt;Page module is enabled for the user. This can be disabled with this switch.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:29;a:6:{s:8:"property";s:19:"classicPageEditMode";s:8:"datatype";s:7:"boolean";s:11:"description";s:287:"Setting this option will not open the Web&gt;Page module but rather load the content elements (normal column/default language) together with the page header in one big form when a page is edited (clicking a page icon in the page tree). This simulates the old behaviour in Classic Backend";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:30;a:6:{s:8:"property";s:18:"hideSubmoduleIcons";s:8:"datatype";s:7:"boolean";s:11:"description";s:95:"(AB) If set then submodule icons will not be shown in the left menu of the Alternative Backend.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:31;a:6:{s:8:"property";s:27:"noOnChangeAlertInTypeFields";s:8:"datatype";s:7:"boolean";s:11:"description";s:184:"When a record field is changed and that field would affect the layout of the form, a message normally pops up, saying that one should save now. If this flag is set, no message pops up.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:32;a:6:{s:8:"property";s:27:"dontShowPalettesOnFocusInAB";s:8:"datatype";s:7:"boolean";s:11:"description";s:92:"If set, palettes are not activated in the TCEFORMs (typ. AB) when focus is moved to a field.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('109', '263', '71860c77c6745379b0d44304d66b6a13', '', 'page', 'a:1:{s:4:"rows";a:6:{i:0;a:6:{s:8:"property";s:3:"mod";s:8:"datatype";s:8:"-&gt;MOD";s:11:"description";s:87:"Options for the modules (overridden by any similar values set for the individual user!)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:3:"RTE";s:8:"datatype";s:8:"-&gt;RTE";s:11:"description";s:51:"This defines configuration for the Rich Text Editor";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:7:"TCEMAIN";s:8:"datatype";s:12:"-&gt;TCEMAIN";s:11:"description";s:64:"Defines configuration for the tce_main class (Typo3 Core Engine)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:7:"TCEFORM";s:8:"datatype";s:12:"-&gt;TCEFORM";s:11:"description";s:95:"This defines extra configuration for the form fields rendered by the tceforms-class in general.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:4:"TSFE";s:8:"datatype";s:9:"-&gt;TSFE";s:11:"description";s:37:"Options for the TSFE front end object";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:4:"user";s:8:"datatype";s:3:"???";s:11:"description";s:65:"This is for custom purposed. You can use if for your own games...";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('110', '263', '7a924c59af67c619fd51493947aa7abb', '', 'page:TSFE', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:23:"jumpUrl_transferSession";s:8:"datatype";s:7:"boolean";s:11:"description";s:901:"If set, the jumpUrl redirection to the URL will be prepended with a parameter that transfers the current fe_users session to that url. This URL should be the Typo3 frontend in the same database, just at another domain (else it makes no sense).\n&nbsp;\nYou can implement it in your own links if you like. This is how you do:\nYou must send the parameter \'FE_SESSION_KEY\' as GET or POST. The parameter looks like this: [fe_user-session-id]-[a hash made to prevent misuse]\nThe parameter can be calculated like this:\n&nbsp;\n$param = &quot;&amp;FE_SESSION_KEY=&quot;.rawurlencode(\n&nbsp;&nbsp;&nbsp;$GLOBALS[&quot;TSFE&quot;]-&gt;fe_user-&gt;id.&quot;-&quot;.\n&nbsp;&nbsp;&nbsp;md5(\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$GLOBALS[&quot;TSFE&quot;]-&gt;fe_user-&gt;id.&quot;/&quot;.\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;$GLOBALS[&quot;TYPO3_CONF_VARS&quot;][&quot;SYS&quot;][&quot;encryptionKey&quot;]\n&nbsp;&nbsp;&nbsp;)\n);";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('111', '263', '30152a9fef0b7133032a96a8da8cc370', '', 'page:mod; beuser:mod', 'a:1:{s:4:"rows";a:5:{i:0;a:6:{s:8:"property";s:24:"web_layout.menu.function";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:109:"(Module Web&gt;Page):\n0 =&gt; &quot;QuickEdit&quot;\n1 =&gt; &quot;Columns&quot;\n2 =&gt; &quot;Languages&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:22:"web_info.menu.function";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:139:"(Module Web&gt;Info):\npage =&gt; &quot;Pagetree overview&quot;\nlog =&gt; &quot;Admin Changelog&quot;\ntsconf =&gt; &quot;Page TSconfig&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:22:"web_func.menu.function";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:108:"(Module Web&gt;Functions):\n1 =&gt; &quot;Import&quot;\n2 =&gt; &quot;Export&quot;\n3 =&gt; &quot;Wizards&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:20:"web_ts.menu.function";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:180:"(Module Web&gt;Template):\n0 =&gt; &quot;Info/Modify&quot;,\n1 =&gt; &quot;Constant editor&quot;,\n2 =&gt; &quot;TypoScript Object browser&quot;,\n3 =&gt; &quot;Template analyzer&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:23:"user_task.menu.function";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:308:"(Module User&gt;Task center):\n&quot;tasks&quot; =&gt; &quot;Tasks&quot;,\n&quot;messages&quot; =&gt; &quot;Messages&quot;,\n&quot;note&quot; =&gt; &quot;Quick Note&quot;,\n&quot;recent&quot; =&gt; &quot;Recent Pages&quot;,\n&quot;mod&quot; =&gt; &quot;Modules&quot;,\n&quot;actions&quot; =&gt; &quot;Actions&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('112', '263', '6d6b3181832d0e42499761f5c48b6b77', '', 'page:mod.SHARED; beuser:mod.SHARED', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:11:"colPos_list";s:8:"datatype";s:46:"(list of integers, blank = don\'t do anything.)";s:11:"description";s:768:"This option lets you specify which columns of tt_content elements should be displayed in the \'Columns\' view of the modules, in particular Web&gt;Page.\nBy default there are four columns, Left, Normal, Right, Border. However most websites use only the Normal column, maybe another also. In that case the remaining columns are not needed. By this option you can specify exactly which of the columns you want to display.\nEach column has a number which ultimately comes from the configuration of the table tt_content, field \'colPos\' found in the tables.php file. This is the values of the four default columns:\nLeft: 1\nNormal: 0\nRight: 2\nBorder: 3\n&nbsp;\n<b>Example:</b>\nThis results in only the Normal and Border column being displayed:\n&nbsp;\nmod.SHARED.colPos_list = 0,3";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('113', '263', '274c2a141d83a6d6e47d64ee7f84b387', '', 'page:mod.web_ts; beuser:mod.web_ts', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:22:"constantEditor.example";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:228:"Controlling appearance of the example image in the Constant editor.\n&nbsp;\n<b>Example:</b>\nThis will display the examples in the top of the page (default is bottom):\n&nbsp;\nmod.web_ts {\n&nbsp;&nbsp;constantEditor.example = top\n}";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('114', '263', '078f19893d51c017f9ce7d86953d91e5', '', 'page:mod.web_modules; beuser:mod.web_modules', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:5:"dmail";s:8:"datatype";s:6:"&nbsp;";s:11:"description";s:665:"(Module: Web&gt;Modules / Direct Mail)\n&nbsp;\nThis is the available properties. They are primarily set as page TSconfig and therefore you should refer to the module itself for documentation:\n&nbsp;\nmod.web_modules.dmail {\n&nbsp;&nbsp;from_email\n&nbsp;&nbsp;from_name\n&nbsp;&nbsp;replyto_email\n&nbsp;&nbsp;replyto_name &nbsp;\n&nbsp;&nbsp;organisation\n&nbsp;&nbsp;sendOptions (isset)\n&nbsp;&nbsp;HTMLParams (isset)\n&nbsp;&nbsp;plainParams (isset)\n&nbsp;&nbsp;userTable\n&nbsp;&nbsp;enablePlain\n&nbsp;&nbsp;enableHTML\n&nbsp;&nbsp;http_username\n&nbsp;&nbsp;http_password\n&nbsp;\n&nbsp;&nbsp;test_tt_address_uids\n&nbsp;\n&nbsp;&nbsp;categories.[integer=bit, 1-31] = label\n}";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('115', '263', '74776a6dd08b834aa9af85e14c2f33dc', '', 'page:mod.web_layout; beuser:mod.web_layout', 'a:1:{s:4:"rows";a:9:{i:0;a:6:{s:8:"property";s:22:"tt_content.colPos_list";s:8:"datatype";s:46:"(list of integers, blank = don\'t do anything.)";s:11:"description";s:263:"See mod.SHARED.colPos_list for details.\nIf non-blank, this list will override the one set by &nbsp;mod.SHARED.colPos_list.\n&nbsp;\n<b>Example:</b>\nThis results in only the Normal and Border column being displayed:\n&nbsp;\nmod.web_layout.tt_content.colPos_list = 0,3";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:21:"tt_content.fieldOrder";s:8:"datatype";s:41:"list of field names from tt_content table";s:11:"description";s:543:"This allows you to specify (and thereby overrule) the preferred order of the fieldnames of the editing forms of the tt_content table (Content Elements). Just specify the list of fields, separated by comma. Then these fields will be listed first and all remaining fields thereafter in their original order.\n&nbsp;\n<b>Example:</b>\nThis results in the \'Text\' field and thereafter \'Header\' field being display as the very first fields instead of the \'Type\' field.\n&nbsp;\nmod.web_layout.tt_content.fieldOrder = bodytext, header\n&nbsp;\n(In one line)";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:17:"editFieldsAtATime";s:8:"datatype";s:4:"int+";s:11:"description";s:242:"Specifies the number of subsequent content elements to load in the editform when clicking the editicon of a content element in the \'Columns\' view of the module. Default values are \'3\' in the Classic backend and \'1\' in the Alternative backend.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:19:"noCreateRecordsLink";s:8:"datatype";s:7:"boolean";s:11:"description";s:71:"If set, the link in the bottom of the page, Create Records, are hidden.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:11:"QEisDefault";s:8:"datatype";s:7:"boolean";s:11:"description";s:90:"If set, then the QuickEditor is the default function in the top of the menu in Web&gt;Page";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:16:"disableSearchBox";s:8:"datatype";s:7:"boolean";s:11:"description";s:63:"Disables the search box in the bottom of the page in the module";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:6;a:6:{s:8:"property";s:17:"disableBigButtons";s:8:"datatype";s:7:"boolean";s:11:"description";s:46:"Disables the big buttons which shows up in the";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:7;a:6:{s:8:"property";s:15:"disableAdvanced";s:8:"datatype";s:7:"boolean";s:11:"description";s:88:"Disables the clear cache advanced function &nbsp;in the bottom of the page in the module";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:8;a:6:{s:8:"property";s:30:"disableNewContentElementWizard";s:8:"datatype";s:7:"boolean";s:11:"description";s:138:"Disables the fact that the new-content-element icons links to the content element wizard and not directly to a blank &quot;NEW&quot; form.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('116', '263', '26d105f3bb4435c3ee936bec1df6b729', '', 'page:mod.web_list; beuser:mod.web_list', 'a:1:{s:4:"rows";a:6:{i:0;a:6:{s:8:"property";s:19:"noCreateRecordsLink";s:8:"datatype";s:7:"boolean";s:11:"description";s:71:"If set, the link in the bottom of the page, Create Records, are hidden.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:17:"alternateBgColors";s:8:"datatype";s:7:"boolean";s:11:"description";s:56:"If set, the background colors of elements will alternate";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:22:"disableSingleTableView";s:8:"datatype";s:7:"boolean";s:11:"description";s:172:"If set, then the links which shows details about tables will not be available (including sorting links on columns titles, because these links jumps to the table-only view).";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:16:"allowedNewTables";s:8:"datatype";s:25:"<i>list of tablenames</i>";s:11:"description";s:366:"If this list is set, then only tables listed here will have a link to &quot;create new&quot; in the page and subpages.\nThis also affects db_new.php (the display of &quot;Create new record&quot;)\n<b>Note:</b> Technically records can be created (eg. by copying/moving), so this is pseudo security. The point is to reduce the number of options for new records visually.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:10:"newWizards";s:8:"datatype";s:7:"boolean";s:11:"description";s:177:"If set, then the new-link over the control panel of the pages and tt_content listings in the List module will link to the wizards and not create a record in the top of the list.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:38:"showClipControlPanelsDespiteOfCMlayers";s:8:"datatype";s:7:"boolean";s:11:"description";s:237:"If set, then the control- and clipboard panels of the module is shown even if the context-popups (ClickMenu) are available. Normally the control- and clipboard panels are disabled (unless extended mode is set) in order to save bandwidth.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('117', '263', '534f5e7bb8661df29758d344ee47f04b', '', 'page:mod.web_view; beuser:mod.web_view', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:4:"type";s:8:"datatype";s:4:"int+";s:11:"description";s:65:"Enter the value of the &amp;type parameter passed to the webpage.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('118', '263', '14bf6725234188138476fdcb63eaba70', '', 'beuser:mod.tools_em', 'a:1:{s:4:"rows";a:1:{i:0;a:6:{s:8:"property";s:14:"allowTVlisting";s:8:"datatype";s:7:"boolean";s:11:"description";s:191:"If set the Technical and Validation listings are available in the EM. Those will evaluate ALL available extensions and that can take many seconds (up to 30) depending on number of extensions.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
