INSERT INTO static_tsconfig_help VALUES ('376', '16', '255d01ae1b0d5003500f17aeccc21a14', '', 'nbsp;', 'a:1:{s:4:"rows";a:6:{i:0;a:6:{s:8:"property";s:15:"specConfs.[pid]";s:8:"datatype";s:1:"-";s:11:"description";s:908:"&quot;specConfs&quot; is an array of objects with properties that can customize certain behaviours of the display of a result row depending on it\'s position in the rootline. For instance you can define that all results which links to pages in a branch from page id 123 should have another page icon displayed. Of you can add a suffix to the class names so you can style that section differently.\n&nbsp;\n<b>Examples:</b>\nIf a page &quot;Contact&quot; is found in a search for &quot;address&quot; and that &quot;Contact&quot; page is in the rootline &quot;Frontpage [ID=23] &gt; About us [ID=45] &gt; Contact [ID=77]&quot; then you should set the pid value to either &quot;77&quot; or &quot;45&quot;. If &quot;45&quot; then all subpages including the &quot;About us&quot; page will have similar configuration.\nIf the pid value is set to 0 (zero) it will apply to all pages.\n&nbsp;\nPlease see the options below.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:24:"specConfs.[pid].pageIcon";s:8:"datatype";s:18:"-&gt;IMAGE cObject";s:11:"description";s:22:"Alternative page icon.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:25:"specConfs.[pid].CSSsuffix";s:8:"datatype";s:6:"string";s:11:"description";s:311:"A string that will be appended to the class-names of all the class-attributes used within the result row presentation. The prefix will be like this:\n&nbsp;\n<b>Example:</b>\nIf &quot;...CSSsuffix = doc&quot; then eg. the class name &quot;tx-indexedsearch-title&quot; will be &quot;tx-indexedsearch-title-doc&quot;";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:18:"search.rootPidList";s:8:"datatype";s:11:"list of int";s:11:"description";s:299:"A list of integer which should be root-pages to search from. Thus you can search multiple branches of the page tree by setting this property to a list of page id numbers.\nIf this value is set to less than zero (eg. -1) searching will happen in ALL of the page tree with no regard to branches at all.";s:7:"default";s:24:"The current root-page id";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:4;a:6:{s:8:"property";s:32:"search.detect_sys_domain_records";s:8:"datatype";s:7:"boolean";s:11:"description";s:86:"If set, then the search results are linked to the proper domains where they are found.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:5;a:6:{s:8:"property";s:39:"search.detect_sys_domain_records.target";s:8:"datatype";s:6:"string";s:11:"description";s:25:"Target for external URLs.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('323', '323', '315ccebcea83a604b0f60107bbf6460a', '', 'page:mod.web_txdanewslettersubscriptionM1; beuser:mod.web_txdanewslettersubscriptionM1', 'a:1:{s:4:"rows";a:3:{i:0;a:6:{s:8:"property";s:15:"menu.function.1";s:8:"datatype";s:7:"boolean";s:11:"description";s:81:"If set to 0 (zero) the &quot;Newsletter categories&quot; item will be unavailable";s:7:"default";s:1:"1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:15:"menu.function.2";s:8:"datatype";s:7:"boolean";s:11:"description";s:75:"If set to 0 (zero) the &quot;Subscriber list&quot; item will be unavailable";s:7:"default";s:1:"1";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:18:"fe_users_fieldList";s:8:"datatype";s:15:"list of strings";s:11:"description";s:229:"A list of fe_users fields to list in the backend for selection. The fields cound be from this list. Fields can only be listed once:\n&nbsp;\nusername,name,address,zip,city,telephone\n&nbsp;\nThe field, &quot;email&quot; is mandatory.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
INSERT INTO static_tsconfig_help VALUES ('324', '323', '9b339e8e0dc059aaad2c3d88fbda4eb3', '', 'tsref:plugin.tx_danewslettersubscription_pi1', 'a:1:{s:4:"rows";a:4:{i:0;a:6:{s:8:"property";s:13:"sorting_field";s:8:"datatype";s:6:"string";s:11:"description";s:153:"Allows you to set a sorting field for the listing of news categories.\nAllows values from the list;\nsorting,starttime,endtime,uid,crdate,title,description";s:7:"default";s:7:"sorting";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:1;a:6:{s:8:"property";s:12:"sorting_desc";s:8:"datatype";s:7:"boolean";s:11:"description";s:38:"If set, sorting is in descending order";s:7:"default";s:1:"0";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:2;a:6:{s:8:"property";s:4:"mode";s:8:"datatype";s:6:"string";s:11:"description";s:370:"Defines the mode. Can be set to blank, &quot;dual&quot; or &quot;login&quot;.\nThe default means that people will have to enter their email address. They will receive an email with a link back to the site which will allow them to subscribe or edit subscription.\nIf &quot;login&quot; then the subscription works for login users only.\nIf &quot;dual&quot; then it uses both.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}i:3;a:6:{s:8:"property";s:16:"mode.noAuthEmail";s:8:"datatype";s:3:"int";s:11:"description";s:421:"Default is 0 (zero)\n&nbsp;\n<b>1:</b>\nIf set non-subscribed users are NOT sent an email but directly guided to the subscription form. Thus there is NOT safety for users submitted correct email addresses or their own address for that sake.\nHowever if a user is already subscribed he will receive an email with a link to the page.\n<b>2:</b>\nLike 1, but where the editing of an existing profile does not require login either.";s:7:"default";s:6:"&nbsp;";s:12:"column_count";i:4;s:16:"is_propertyTable";i:1;}}}', '');
